INSERT INTO `greek` (`id`, `word_id`, `word`, `pron`, `origin`, `source`, `kind`, `av`, `count`, `definition`) VALUES
(0, NULL, '', '', '', '', '', '', 0, '\r\n [kosong]\r\n\r\n'),
(1, 'a', '\\~a\\~ a', '\\@al''-fah\\@', 'of Hebrew origin \\^a\\^', 'TDNT - 1:1,*', 'letter', 'Alpha 4', 4, ' 1) first letter of Greek alphabet\r\n 2) Christ is the Alpha to indicate that he is the beginning and the end\r\n'),
(2, 'Aaron', '\\~Aarwn\\~ Aaron', '\\@ah-ar-ohn''\\@', 'of Hebrew origin \\\\0175\\\\ \\^Nrha\\^', 'TDNT - 1:3,1', 'pr n m', 'Aaron 5', 5, ' Aaron = "light-bringer"\r\n\r\n 1) the brother of Moses, the first high priest of Israel and head\r\n    of the whole priestly order\r\n'),
(3, 'Abaddon', '\\~Abaddwn\\~ Abaddon', '\\@ab-ad-dohn''\\@', 'of Hebrew origin \\\\011\\\\ \\^Nwdba\\^', 'TDNT - 1:4,1', 'pr n m', 'Abaddon 1', 1, ' Abaddon = "destruction"\r\n\r\n 1) ruin\r\n 2) destruction\r\n 3) the place of destruction\r\n 4) the name of the angel-prince of the infernal regions, the\r\n    minister of death and the author of havoc on the earth\r\n'),
(4, 'abares', '\\~abarhv\\~ abares', '\\@ab-ar-ace''\\@', 'from \\\\1\\\\ (as a negative particle) and \\\\922\\\\', '', 'adj', 'not burdensome 1', 1, ' 1) not burdensome\r\n 2) not heavy\r\n 3) light without weight\r\n'),
(5, 'Abba', '\\~Abba\\~ Abba', '\\@ab-bah''\\@', 'of Aramaic origin \\\\02\\\\ \\^aba\\^', 'TDNT - 1:5,1', 'n', 'Abba 3', 3, ' Abba = "father"\r\n\r\n 1) father, customary title used of God in prayer. Whenever it occurs\r\n    in the New Testament it has the Greek interpretation joined to it,\r\n    that is apparently to be explained by the fact that the Chaldee\r\n    "ABBA" through frequent use in prayer, gradually acquired the\r\n    nature of a most sacred proper name, to which the Greek speaking\r\n    Jews added the name from their own tongue.\r\n'),
(6, 'Abel', '\\~Abel\\~ Abel', '\\@ab''-el\\@', 'of Hebrew origin \\\\01893\\\\ \\^lbh\\^', 'TDNT - *,2', 'pr n m', 'Abel 4', 4, ' Abel = "vanity (that is: transitory)"\r\n\r\n 1) the second son of Adam, murdered by his brother Cain\r\n'),
(7, 'Abia', '\\~Abia\\~ Abia', '\\@ab-ee-ah''\\@', 'of Hebrew origin \\\\029\\\\ \\^hyba\\^', '', 'pr n m', 'Abia 3', 3, ' Abijah = "my father is Jah (Jehovah)"\r\n\r\n 1) a priest, the head of a priestly family from whom when David\r\n    divided the priests into 24 classes, Abia was the 8th order\r\n 2) son and successor to Rehoboam on the throne of Judah\r\n'),
(8, 'Abiathar', '\\~Abiayar\\~ Abiathar', '\\@ab-ee-ath''-ar\\@', 'of Hebrew origin \\\\054\\\\ \\^rtyba\\^', '', 'pr n m', 'Abiathar 1', 1, ' Abiathar = "father of abundance"\r\n\r\n 1) the name of a high priest\r\n'),
(9, 'Abilene', '\\~Abilhnh\\~ Abilene', '\\@ab-ee-lay-nay''\\@', 'of foreign origin, cf \\\\058\\\\', '', 'pr n loc', 'Abilene 1', 1, ' Abilene = "grassy meadow"\r\n\r\n 1) a region of Syria between Lebanon and Hermon towards Phoenicia, 18 miles\r\n    (29 km) from Damascus and 37 miles (60 km) from Heliopolis\r\n'),
(10, 'Abioud', '\\~Abioud\\~ Abioud', '\\@ab-ee-ood''\\@', 'of Hebrew origin \\\\031\\\\ \\^dwhyba\\^', '', 'pr n loc', 'Abiud 2', 2, ' Abiud = "my father is majesty"\r\n\r\n 1) the son of Bela and grandson of Benjamin (\\\\#1Ch 8:3\\\\)\r\n'),
(11, 'Abraam', '\\~Abraam\\~ Abraam', '\\@ab-rah-am''\\@', 'of Hebrew origin \\\\085\\\\ \\^Mhrba\\^', 'TDNT - 1:8,2', 'pr n m', 'Abraham 73', 73, ' Abraham = "father of a multitude"\r\n\r\n 1) the son of Terah and the founder of the Jewish nation\r\n'),
(12, 'abussos', '\\~abussov\\~ abussos', '\\@ab''-us-sos\\@', 'from \\\\1\\\\ (as a negative particle) and a variation of \\\\1037\\\\', 'TDNT - 1:9,2', 'n f', 'bottomless pit 5, deep 2, bottomless 2', 9, ' 1) bottomless\r\n 2) unbounded\r\n 3) the abyss\r\n    3a) the pit\r\n    3b) the immeasurable depth\r\n    3c) of Orcus, a very deep gulf or chasm in the lowest parts\r\n        of the earth used as the common receptacle of the dead and\r\n        especially as the abode of demons\r\n'),
(13, 'Agabos', '\\~Agabov\\~ Agabos', '\\@ag''-ab-os\\@', 'of Hebrew origin, cf \\\\02285\\\\ \\^owbga\\^', '', 'pr n m', 'Agabus 2', 2, ' Agabus = "locust"\r\n\r\n 1) a Christian prophet\r\n'),
(14, 'agathoergeo', '\\~agayoergew\\~ agathoergeo', '\\@ag-ath-er-gheh''-o\\@', 'from \\\\18\\\\ and \\\\2041\\\\', 'TDNT - 1:17,3', 'v', 'do good 1', 1, ' 1) to work good, to do good, to do well, act rightly\r\n'),
(15, 'agathopoieo', '\\~agayopoiew\\~ agathopoieo', '\\@ag-ath-op-oy-eh''-o\\@', 'from \\\\17\\\\', 'TDNT - 1:17,3', 'v', 'do good 7, well doing 2, do well 2', 11, ' 1) to do good, do something which profits others\r\n    1a) to be a good help to someone\r\n    1b) to do someone a favour\r\n    1c) to benefit\r\n 2) to do well, do right\r\n'),
(16, 'agathopoiia', '\\~agayopoiia\\~ agathopoiia', '\\@ag-ath-op-oy-ee''-ah\\@', 'from \\\\17\\\\', 'TDNT - 1:17,3', 'n f', 'well doing 1', 1, ' 1) a course of right action, well doing, virtue\r\n'),
(17, 'agathopoios', '\\~agayopoiov\\~ agathopoios', '\\@ag-ath-op-oy-os''\\@', 'from \\\\18\\\\ and \\\\4160\\\\', 'TDNT - 1:17,3', 'adj', 'do well 1', 1, ' 1) acting rightly, doing well, virtuous\r\n'),
(18, 'agathos', '\\~agayov\\~ agathos', '\\@ag-ath-os''\\@', 'a primary word', 'TDNT - 1:10,3', 'adj', 'good 77, good thing 14, that which is good + \\\\3588\\\\ 8, the thing which is good + \\\\3588\\\\ 1, well 1, benefit 1', 102, ' 1) of good constitution or nature\r\n 2) useful, salutary\r\n 3) good, pleasant, agreeable, joyful, happy\r\n 4) excellent, distinguished\r\n 5) upright, honourable\r\n'),
(19, 'agathosune', '\\~agaywsunh\\~ agathosune', '\\@ag-ath-o-soo''-nay\\@', 'from \\\\18\\\\', 'TDNT - 1:18,3', 'n f', 'goodness 4', 4, ' 1) uprightness of heart and life, goodness, kindness\r\n'),
(20, 'agalliasis', '\\~agalliasiv\\~ agalliasis', '\\@ag-al-lee''-as-is\\@', 'from \\\\21\\\\', 'TDNT - 1:19,4', 'n f', 'gladness 3, joy 1, exceeding joy 1', 5, ' 1) exultation, extreme joy, gladness\r\n\r\n At feasts, people were anointed with the "oil of gladness".  Paul, in\r\n \\\\#Heb 1:9\\\\, is alluding to this inaugural ceremony of anointing, and\r\n uses it as an emblem of the divine power and majesty to which to\r\n which the Son of God has been exalted.\r\n'),
(21, 'agalliao', '\\~agalliaw\\~ agalliao', '\\@ag-al-lee-ah''-o\\@', 'from agan (much) and \\\\242\\\\', 'TDNT - 1:19, *', 'v', 'rejoice 7, be exceeding glad 1, be glad 1, greatly rejoice 1, with exceeding joy 1', 11, ' 1) to exult, rejoice exceedingly, be exceeding glad\r\n'),
(22, 'agamos', '\\~agamov\\~ agamos', '\\@ag''-am-os\\@', 'from \\\\1\\\\ (as a negative particle) and \\\\1062\\\\', '', 'adj', 'unmarried 4', 4, ' 1) unmarried, unwedded, single\r\n'),
(23, 'aganakteo', '\\~aganaktew\\~ aganakteo', '\\@ag-an-ak-teh''-o\\@', 'from agan (much) and achthos (grief, akin to the base of \\\\43\\\\)', '', 'v', 'have indignation 2, be much displeased 2, with indignation 2, be sore displeased 1', 7, ' 1) to be indignant, moved with indignation, be very displeased\r\n'),
(24, 'aganaktesis', '\\~aganakthsiv\\~ aganaktesis', '\\@ag-an-ak''-tay-sis\\@', 'from \\\\23\\\\', '', 'n f', 'indignation 1', 1, ' 1) indignation, irritation, vexation\r\n'),
(25, 'agapao', '\\~agapaw\\~ agapao', '\\@ag-ap-ah''-o\\@', 'perhaps from agan (much) [or cf \\\\05689\\\\ \\^bge\\^]', 'TDNT - 1:21,5', 'v', 'love 135, beloved 7', 142, ' 1) of persons\r\n    1a) to welcome, to entertain, to be fond of, to love dearly\r\n 2) of things\r\n    2a) to be well pleased, to be contented at or with a thing\r\n\r\n Sinonim   : Lihat Definisi \\\\5914\\\\\r\n'),
(26, 'agape', '\\~agaph\\~ agape', '\\@ag-ah''-pay\\@', 'from \\\\25\\\\', 'TDNT - 1:21,5', 'n f', 'love 86, charity 27, dear 1, charitably+ \\\\2596\\\\ 1, feast of charity 1', 116, ' 1) brotherly love, affection, good will, love, benevolence\r\n 2) love feasts\r\n'),
(27, 'agapetos', '\\~agaptov\\~ agapetos', '\\@ag-ap-ay-tos''\\@', 'from \\\\25\\\\', 'TDNT - 1:21,5', 'adj', 'beloved 47, dearly beloved 9, well beloved 3, dear 3', 62, ' 1) beloved, esteemed, dear, favourite, worthy of love\r\n'),
(28, 'Hagar', '\\~Agar\\~ Hagar', '\\@ag''-ar\\@', 'of Hebrew origin \\\\01904\\\\ \\^rgh\\^', 'TDNT - 1:55,10', 'pr n f', 'Agar 2', 2, ' Hagar = "flight"\r\n\r\n 1) Abraham''s concubine and mother to Ishmael\r\n'),
(29, 'aggareuo', '\\~aggareuw\\~ aggareuo', '\\@ang-ar-yew''-o\\@', 'of foreign origin, cf \\\\0104\\\\', '', 'v', 'compel to go 3', 3, ' 1) to employ a courier, dispatch a mounted messenger, press into\r\n    public service, compel to go\r\n\r\n In Persia, mounted couriers were kept at regular intervals\r\n throughout Persia for carrying the royal dispatches.\r\n'),
(30, 'aggeion', '\\~aggeion\\~ aggeion', '\\@ang-eye''-on\\@', 'from aggos (a pail, perhaps as bent, cf base of \\\\43\\\\)', '', 'n n', 'vessel 2', 2, ' 1) a vessel, receptacle, a pail, a reservoir\r\n'),
(31, 'aggelia', '\\~aggelia\\~ aggelia', '\\@ang-el-ee''-ah\\@', 'from \\\\32\\\\', 'TDNT - 1:56,10', 'n f', 'message 1', 1, ' 1) message, announcement, news\r\n 2) a proclamation, command, order\r\n'),
(32, 'aggelos', '\\~aggelov\\~ aggelos', '\\@ang''-el-os\\@', 'from aggello [probably derived from \\\\71\\\\, cf \\\\34\\\\] (to bring tidings)', 'TDNT - 1:74,12', 'n m', 'angel 179, messenger 7', 186, ' 1) a messenger, envoy, one who is sent, an angel, a messenger\r\n    from God\r\n'),
(33, 'age', '\\~age\\~ age', '\\@ag''-eh\\@', 'imperative of \\\\71\\\\', '', 'inter', 'go to 2', 2, ' 1) go to, come!, come now!\r\n'),
(34, 'agele', '\\~agelh\\~ agele', '\\@ag-el''-ay\\@', 'from \\\\71\\\\ [cf \\\\32\\\\]', '', 'n f', 'herd 8', 8, ' 1) herd or oxen or cattle, a herd or company\r\n'),
(35, 'agenealogetos', '\\~agenealoghtov\\~ agenealogetos', '\\@ag-en-eh-al-og''-ay-tos\\@', 'from \\\\1\\\\ (as negative particle) and \\\\1075\\\\', 'TDNT - 1:665,114', 'adj', 'without descent 1', 1, ' 1) one whose descent there is no record of, without genealogy\r\n'),
(36, 'agenes', '\\~agenhv\\~ agenes', '\\@ag-en-ace''\\@', 'from \\\\1\\\\ (as negative particle) and \\\\1085\\\\', '', 'adj', 'base things 1', 1, ' 1) ignoble, coward, mean, base, of no family, that is: low born\r\n'),
(37, 'hagiazo', '\\~agiazw\\~ hagiazo', '\\@hag-ee-ad''-zo\\@', 'from \\\\40\\\\', 'TDNT - 1:111,14', 'v', 'sanctify 26, hallow 2, be holy 1', 29, ' 1) to render or acknowledge, or to be venerable or hallow\r\n 2) to separate from profane things and dedicate to God\r\n    2a) consecrate things to God\r\n    2b) dedicate people to God\r\n 3) to purify\r\n    3a) to cleanse externally\r\n    3b) to purify by expiation: free from the guilt of sin\r\n    3c) to purify internally by renewing of the soul\r\n'),
(38, 'hagiasmos', '\\~agiasmov\\~ hagiasmos', '\\@hag-ee-as-mos''\\@', 'from \\\\37\\\\', 'TDNT - 1:113,14', 'n m', 'holiness 5, sanctification 5', 10, ' 1) consecration, purification\r\n 2) the effect of consecration\r\n    2a) sanctification of heart and life\r\n'),
(39, 'hagion', '\\~agion\\~ hagion', '\\@hag''-ee-on\\@', 'neuter of \\\\40\\\\', '', 'adj', 'sanctuary 4, holy place 3, holiest of all 3, holinest 1', 11, ' 1) reverend, worthy of veneration\r\n    1a) of things which on account of some connection with God\r\n        possess a certain distinction and claim to reverence, as\r\n        places sacred to God which are not to be profaned\r\n    1b) of persons whose services God employs, for example, apostles\r\n 2) set apart for God, to be as it were, exclusively his\r\n 3) services and offerings\r\n    3a) prepared for God with solemn rite, pure, clean\r\n 4) in a moral sense, pure sinless upright holy\r\n'),
(40, 'hagios', '\\~agiov\\~ hagios', '\\@hag''-ee-os\\@', 'from hagos (an awful thing) [cf \\\\53\\\\, \\\\2282\\\\]', 'TDNT - 1:88,14', 'adj', 'holy 161, saints 61, Holy One 4, misc 3', 229, ' 1) most holy thing, a saint\r\n\r\n Sinonim   : Lihat Definisi \\\\5878\\\\\r\n'),
(41, 'hagiotes', '\\~agiothv\\~ hagiotes', '\\@hag-ee-ot''-ace\\@', 'from \\\\40\\\\', 'TDNT - 1:114,14', 'n f', 'holiness 1', 1, ' 1) sanctity\r\n 2) in a moral sense: holiness\r\n'),
(42, 'hagiosune', '\\~agiwsunh\\~ hagiosune', '\\@hag-ee-o-soo''-nay\\@', 'from \\\\40\\\\', 'TDNT - 1:114,14', 'n f', 'holiness 3', 3, ' 1) majesty, holiness\r\n 2) moral purity\r\n'),
(43, 'agkale', '\\~agkalh\\~ agkale', '\\@ang-kal''-ay\\@', 'from agkos (a bend, "ache")', '', 'n f', 'arm 1', 1, ' 1) the curve or inner angle of the arm, the bent arm\r\n 2) anything closely enfolding, as the arms of the sea, etc.\r\n'),
(44, 'agkistron', '\\~agkistron\\~ agkistron', '\\@ang''-kis-tron\\@', 'from the same as \\\\43\\\\', '', 'n n', 'hook 1', 1, ' 1) a fishing hook, a small hook\r\n'),
(45, 'agkura', '\\~agkura\\~ agkura', '\\@ang''-koo-rah\\@', 'from the same as \\\\43\\\\', '', 'n f', 'anchor 4', 4, ' 1) an anchor\r\n 2) metaph. any stay or safeguard\r\n\r\n Ancient anchors resembled modern ones in form: they were of iron,\r\n provided with a stock, and with two teeth-like extremities often\r\n but by no means without flukes.\r\n'),
(46, 'agnaphos', '\\~agnafov\\~ agnaphos', '\\@ag''-naf-os\\@', 'from \\\\1\\\\ (as a negative particle) and the same as \\\\1102\\\\', '', 'adj', 'new 2', 2, ' 1) unmilled, unfulled, undressed, unprocessed, new\r\n'),
(47, 'hagneia', '\\~agneia\\~ hagneia', '\\@hag-ni''-ah\\@', 'from \\\\53\\\\', 'TDNT - 1:123,19', 'n f', 'purity 2', 2, ' 1) purity, sinlessness of life\r\n'),
(48, 'hagnizo', '\\~agnizw\\~ hagnizo', '\\@hag-nid''-zo\\@', 'from \\\\53\\\\', 'TDNT - 1:123,19', 'v', 'purify 5, purify (one''s) self 2', 7, ' 1) ceremonially\r\n    1a) to make pure, purify, cleanse\r\n 2) morally\r\n'),
(49, 'hagnismos', '\\~agnismov\\~ hagnismos', '\\@hag-nis-mos''\\@', 'from \\\\48\\\\', 'TDNT - 1:124,19', 'n m', 'purification 1', 1, ' 1) purification, expiation\r\n'),
(50, 'agnoeo', '\\~agnoew\\~ agnoeo', '\\@ag-no-eh''-o\\@', 'from \\\\1\\\\ (as a negative particle) and \\\\3539\\\\', 'TDNT - 1:115,18', 'v', 'be ignorant 7, ignorant 4, know not 4, understand not 3, ignorantly 2, unknown 2', 22, ' 1) to be ignorant, not to know\r\n 2) not to understand, unknown\r\n 3) to err or sin through mistake, to be wrong\r\n'),
(51, 'agnoema', '\\~agnohma\\~ agnoema', '\\@ag-no''-ay-mah\\@', 'from \\\\50\\\\', 'TDNT - 1:115,18', 'n n', 'error 1', 1, ' 1) a sin committed through ignorance or thoughtlessness\r\n\r\n Sinonim   : Lihat Definisi \\\\3879\\\\\r\n'),
(52, 'agnoia', '\\~agnoia\\~ agnoia', '\\@ag''-noy-ah\\@', 'from \\\\50\\\\', 'TDNT - 1:116,18', 'n f', 'ignorance 4', 4, ' 1) lack of knowledge, ignorance\r\n    1a) esp.  of divine things\r\n    1b) of moral blindness\r\n'),
(53, 'hagnos', '\\~agnov\\~ hagnos', '\\@hag-nos''\\@', 'from the same as \\\\40\\\\', 'TDNT - 1:122,19', 'adj', 'pure 4, chaste 3, clear 1', 8, ' 1) exciting reverence, venerable, sacred\r\n 2) pure\r\n    2a) pure from carnality, chaste, modest\r\n    2b) pure from every fault, immaculate\r\n    2c) clean\r\n\r\n Sinonim   : Lihat Definisi \\\\5878\\\\\r\n'),
(54, 'hagnotes', '\\~agnothv\\~ hagnotes', '\\@hag-not''-ace\\@', 'from \\\\53\\\\', 'TDNT - 1:124,19', 'n f', 'pureness 1', 1, ' 1) purity, chastity, uprightness of life\r\n'),
(55, 'hagnos', '\\~agnwv\\~ hagnos', '\\@hag-noce''\\@', 'from \\\\53\\\\', '', 'adv', 'sincerely 1', 1, ' 1) chaste, clean, pure, with sincerity\r\n'),
(56, 'agnosia', '\\~agnwsia\\~ agnosia', '\\@ag-no-see''-ah\\@', 'from \\\\1\\\\ (as negative particle) and \\\\1108\\\\', 'TDNT - 1:116,18', 'n f', 'not the knowledge 1, ignorance 1', 2, ' 1) not knowing, ignorance\r\n'),
(57, 'agnostos', '\\~agnwstov\\~ agnostos', '\\@ag''-noce-tos''\\@', 'from \\\\1\\\\ (as negative particle) and \\\\1110\\\\', 'TDNT - 1:119,18', 'adj', 'unknown 1', 1, ' 1) unknown, forgotten\r\n ++++\r\n Epimenides, a certain Cretan, came to Athens to stop a plague. He\r\n offered a sheep on altars the born no name of no god but to the\r\n "unknown god". At least one of the altars survived to Paul''s day.\r\n'),
(58, 'agora', '\\~agora\\~ agora', '\\@ag-or-ah''\\@', 'from ageiro (to gather, probably akin to \\\\1453\\\\)', '', 'n f', 'market 6, marketplace 4, street 1', 11, ' 1) any assembly, especially of the people\r\n 2) the place of assembly\r\n    2a) for public debating,\r\n    2b) for elections\r\n    2c) for trials\r\n    2d) for buying and selling\r\n    2e) for all kinds of business\r\n 3) market place, street\r\n'),
(59, 'agorazo', '\\~agorazw\\~ agorazo', '\\@ag-or-ad''-zo\\@', 'from \\\\58\\\\', 'TDNT - 1:124,19', 'v', 'buy 28, redeem 3', 31, ' 1) to be in the market place, to attend it\r\n 2) to do business there, buy or sell\r\n 3) of idle people: to haunt the market place, lounge there\r\n'),
(60, 'agoraios', '\\~agoraiov\\~ agoraios', '\\@ag-or-ah''-yos\\@', 'from \\\\58\\\\', '', 'adj', 'baser sort 1, law 1', 2, ' 1) in, of or belonging to the market place\r\n 2) frequenting the market place\r\n    2a) hucksters, petty traffickers, retail dealers\r\n    2b) idlers, loungers, the common sort, low, mean vulgar\r\n 3) generally, proper to the assembly, suited to forensic\r\n    speaking, business-like transactions\r\n'),
(61, 'agra', '\\~agra\\~ agra', '\\@ag''-rah\\@', 'from \\\\71\\\\', '', 'n f', 'draught 2', 2, ' 1) a catching, hunting\r\n 2) that which is taken in hunting, the booty, prey\r\n    2a) of birds or beasts, game\r\n    2b) of fish, a draught or haul\r\n'),
(62, 'agrammatos', '\\~agrammatov\\~ agrammatos', '\\@ag-ram-mat-os\\@', 'from \\\\1\\\\ (as negative particle) and \\\\1121\\\\', '', 'adj', 'unlearned 1', 1, ' 1) illiterate, unlearned\r\n'),
(63, 'agrauleo', '\\~agraulew\\~ agrauleo', '\\@ag-row-leh''-o\\@', 'from \\\\68\\\\ and \\\\832\\\\ (in the sense of \\\\833\\\\)', '', 'v', 'abide in the field 1', 1, ' 1) to live in the fields, be under the open sky, even at night\r\n'),
(64, 'agreuo', '\\~agreuw\\~ agreuo', '\\@ag-rew''-o\\@', 'from \\\\61\\\\', '', 'v', 'catch 1', 1, ' 1) to hunt, to take by hunting, catch\r\n 2) metaph. to hunt after, pursue eagerly\r\n'),
(65, 'agrielaios', '\\~agrielaiov\\~ agrielaios', '\\@ag-ree-el''-ah-yos\\@', 'from \\\\66\\\\ and \\\\1636\\\\', '', 'adj', 'wild olive tree 1, olive tree which is wild 1', 2, ' 1) of or belonging to the oleaster or wild olive\r\n 2) the oleaster, wild olive tree\r\n'),
(66, 'agrios', '\\~agriov\\~ agrios', '\\@ag''-ree-os\\@', 'from \\\\68\\\\', '', 'adj', 'wild 2, raging 1', 3, ' 1) living or growing in the fields or woods\r\n    1a) of animals, wild, savage\r\n    1b) of countries, wild, uncultivated, unreclaimed\r\n 2) of men and animals in a moral sense, wild savage, fierce\r\n    2a) boorish, rude\r\n    2b) of any violent passion, vehement, furious\r\n'),
(67, 'Agrippas', '\\~Agrippav\\~ Agrippas', '\\@ag-rip''-pas\\@', 'apparently from \\\\66\\\\ and \\\\2462\\\\', '', 'n pr m', 'Agrippa 12', 12, ' Agrippa = "Hero like"\r\n\r\n 1) Name of a ruling family in Israel at the time of Christ\r\n\r\n See \\\\2264\\\\ for discussion of the Herods.\r\n'),
(68, 'agros', '\\~agrov\\~ agros', '\\@ag-ros''\\@', 'from \\\\71\\\\', '', 'n m', 'field 22, country 8, land 4, farm 1, piece of ground 1', 36, ' 1) land\r\n    1a) the field, the country\r\n    1b) a piece of land, bit of tillage\r\n    1c) the farms, country seats, neighbouring hamlets\r\n'),
