(494, 'Antipatris', '\\~Antipatriv\\~ Antipatris', '\\@an-tip-at-rece''\\@', 'from the same as \\\\493\\\\', '', 'n pr loc', 'Antipatris 1', 1, ' Antipatris = "for forefather"\r\n\r\n 1) a city situated between Joppa and Caesarea, in a very fertile\r\n    region, not far from the coast; rebuilt by Herod the Great and\r\n    named Antipatris in honour of his father Antipater\r\n'),
(495, 'antiperan', '\\~antiperan\\~ antiperan', '\\@an-tee-per''-an\\@', 'from \\\\473\\\\ and \\\\4008\\\\', '', 'adv', 'over against 1', 1, ' 1) over against, on the opposite shore, on the other side\r\n'),
(496, 'antipipto', '\\~antipiptw\\~ antipipto', '\\@an-tee-pip''-to\\@', 'from \\\\473\\\\ and \\\\4098\\\\ (including its alternate)', '', 'v', 'resist 1', 1, ' 1) to fall upon, run against\r\n 2) to be adverse, oppose, strive against\r\n'),
(497, 'antistrateuomai', '\\~antistrateuomai\\~ antistrateuomai', '\\@an-tee-strat-yoo''-om-ahee\\@', 'from \\\\473\\\\ and \\\\4754\\\\', '', 'v', 'war against 1', 1, ' 1) to make a military expedition, or take the field, against anyone\r\n 2) to oppose, war against\r\n'),
