(707, 'Arimathaia', '\\~Arimayaia\\~ Arimathaia', '\\@ar-ee-math-ah''-ee-ah\\@', 'of Hebrew origin \\\\07414\\\\ \\^Mytmrh\\^', '', 'n pr loc', 'Arimathaea 4', 4, ' Arimathaea = "heights"\r\n\r\n 1) the name of several cities in Palestine The one mentioned in\r\n    \\\\#Mt 27:57; Mr 15:43; Lu 23:51; Joh 19:38\\\\ appears to have been the same\r\n    as the birthplace of Samuel in Mount Ephraim.\r\n'),
(708, 'Aristarchos', '\\~Aristarcov\\~ Aristarchos', '\\@ar-is''-tar-khos\\@', 'from the same as \\\\712\\\\ and \\\\757\\\\', '', 'n pr m', 'Aristarchus 5', 5, ' Aristarchus = "the best ruler"\r\n\r\n 1) a certain Christian of Thessalonica, and accompanied Paul on his\r\n    third missionary journey\r\n'),
(709, 'aristao', '\\~aristaw\\~ aristao', '\\@ar-is-tah''-o\\@', 'from \\\\712\\\\', '', 'v', 'dine 3', 3, ' 1) to breakfast\r\n 2) by later usage, to dine\r\n'),
(710, 'aristeros', '\\~aristerov\\~ aristeros', '\\@ar-is-ter-os''\\@', 'apparently a comparative of the same as \\\\712\\\\', '', 'adj', 'left hand 1, left 1, on the left 1', 3, ' 1) left\r\n'),
