(1065, 'ge', '\\~ge\\~ ge', '\\@gheh\\@', 'a primary particle of emphasis or qualification (often used with other particles prefixed)', '', 'particle', 'yet 2, at least 1, beside 1, doubtless 1, not tr 4', 9, ' 1) indeed, truly, at least\r\n 2) even\r\n 3) if indeed, seeing that\r\n'),
(1066, 'Gedeon', '\\~gedewn\\~ Gedeon', '\\@ghed-eh-own''\\@', 'of Hebrew origin \\\\01439\\\\ \\^Nwedg\\^', '', 'n pr m', 'Gedeon 1', 1, ' Gideon = "the cutter down"\r\n\r\n 1) a of the judge of Israel who delivered them from the Midianites\r\n'),
(1067, 'geenna', '\\~geenna\\~ geenna', '\\@gheh''-en-nah\\@', 'of Hebrew origin \\\\01516\\\\ and \\\\02011\\\\', 'TDNT - 1:657,113', 'n f', 'hell 9, hell fire + \\\\3588\\\\ + \\\\4442\\\\ 3', 12, ' 1) Hell is the place of the future punishment call "Gehenna" or\r\n    "Gehenna of fire". This was originally the valley of Hinnom,\r\n    south of Jerusalem, where the filth and dead animals of the\r\n    city were cast out and burned; a fit symbol of the wicked and\r\n    their future destruction.\r\n'),
(1068, 'Gethsemane', '\\~geyshmani\\~ Gethsemane', '\\@gheth-say-man-ay''\\@', 'of Aramaic origin, cf \\\\01660\\\\ and \\\\08081\\\\ \\^ynmv-tg\\^', '', 'n pr loc', 'Gethsemane 2', 2, ' Gethsemane = "an oil press"\r\n\r\n 1) the name of a place at the foot of the Mount of Olives,\r\n    beyond the torrent Kidron\r\n'),
