(2037, 'Erastos', '\\~Erastov\\~ Erastos', '\\@er''-as-tos\\@', 'from erao (to love)', '', 'n pr m', 'Erastus 3', 3, ' Erastus = "beloved"\r\n\r\n 1) a companion of the apostle Paul\r\n 2) the chamberlain or rather the public treasurer, of Corinth\r\n'),
(2038, 'ergazomai', '\\~ergazomai\\~ ergazomai', '\\@er-gad''-zom-ahee\\@', 'middle voice from \\\\2041\\\\', 'TDNT - 2:635,251', 'v', 'work 22, wrought 7, do 3, minister about 1, forbear working + \\\\3361\\\\ 1, labour for 1, labour 1, commit 1, trade by 1, trade 1', 39, ' 1) to work, labour, do work\r\n 2) to trade, to make gains by trading, "do business"\r\n 3) to do, work out\r\n    3a) exercise, perform, commit\r\n    3b) to cause to exist, produce\r\n 4) to work for, earn by working, to acquire\r\n'),
(2039, 'ergasia', '\\~ergasia\\~ ergasia', '\\@er-gas-ee''-ah\\@', 'from \\\\2040\\\\', 'TDNT - 2:635,251', 'n f', 'gain 3, craft 1, diligence 1, work 1', 6, ' 1) a working, performing\r\n 2) work, business\r\n 3) gain got by work, profit\r\n 4) endeavour, pains\r\n'),
