(2690, 'katastrepho', '\\~katastrefw\\~ katastrepho', '\\@kat-as-tref''-o\\@', 'from \\\\2596\\\\ and \\\\4762\\\\', 'TDNT - 7:715,1093', 'v', 'overthrow 2', 2, ' 1) to turn over, turn under\r\n    1a) the soil with a plough\r\n 2) to overturn, overthrow, throw down\r\n'),
(2691, 'katastreniao', '\\~katastrhniaw\\~ katastreniao', '\\@kat-as-tray-nee-ah''-o\\@', 'from \\\\2596\\\\ and \\\\4763\\\\', 'TDNT - 3:631,420', 'v', 'begin to wax wanton against 1', 1, ' 1) to feel the impulses of sexual desire\r\n'),
(2692, 'katastrophe', '\\~katastrofh\\~ katastrophe', '\\@kat-as-trof-ay''\\@', 'from \\\\2690\\\\', 'TDNT - 7:715,1093', 'n f', 'subverting 1, overthrow 1', 2, ' 1) overthrow, destruction\r\n    1a) of cities\r\n 2) metaph. of the extinction of a spirit of consecration\r\n'),
(2693, 'katastronnumi', '\\~katastrwnnumi\\~ katastronnumi', '\\@kat-as-trone''-noo-mee\\@', 'from \\\\2596\\\\ and \\\\4766\\\\', '', 'v', 'overthrow 1', 1, ' 1) to strew over (the ground)\r\n 2) to prostrate, slay, (cf to lay low)\r\n'),
(2694, 'katasuro', '\\~katasurw\\~ katasuro', '\\@kat-as-oo''-ro\\@', 'from \\\\2596\\\\ and \\\\4951\\\\', '', 'v', 'hale 1', 1, ' 1) to draw down, pull down\r\n 2) to draw along, drag forcibly\r\n'),
(2695, 'katasphatto', '\\~katasfattw\\~ katasphatto', '\\@kat-as-fat''-to\\@', 'from \\\\2596\\\\ and \\\\4969\\\\', '', 'v', 'slay 1', 1, ' 1) to kill off, to slaughter\r\n'),
(2696, 'katasphragizo', '\\~katasfragizw\\~ katasphragizo', '\\@kat-as-frag-id''-zo\\@', 'from \\\\2596\\\\ and \\\\4972\\\\', 'TDNT - 7:939,1127', 'v', 'seal 1', 1, ' 1) to cover with a seal, to close up, close with a seal\r\n'),
(2697, 'kataschesis', '\\~katascesiv\\~ kataschesis', '\\@kat-as''-khes-is\\@', 'from \\\\2722\\\\', '', 'n f', 'possession 2', 2, ' 1) a holding back, hindering\r\n 2) a holding fast, possession\r\n'),
(2698, 'katatithemi', '\\~katatiyhmi\\~ katatithemi', '\\@kat-at-ith''-ay-mee\\@', 'from \\\\2596\\\\ and \\\\5087\\\\', '', 'v', 'lay 1, show 1, do 1', 3, ' 1) to lay down, deposit, lay up\r\n 2) to lay by or up for one''s self, for future use\r\n 3) to lay up favour for one''s self with any one, to gain favour\r\n    with (to do something for one which may win favour)\r\n'),
(2699, 'katatome', '\\~katatomh\\~ katatome', '\\@kat-at-om-ay''\\@', 'from a compound of \\\\2596\\\\ and temno (to cut)', 'TDNT - 8:109,1169', 'n f', 'concision 1', 1, ' 1) to cut up, mutilation\r\n'),
(2700, 'katatoxeuo', '\\~katatoxeuw\\~ katatoxeuo', '\\@kat-at-ox-yoo''-o\\@', 'from \\\\2596\\\\ and a derivative of \\\\5115\\\\', '', 'v', 'thrust through 1', 1, ' 1) to shoot down or thrust through with an arrow\r\n'),
(2701, 'katatrecho', '\\~katatrecw\\~ katatrecho', '\\@kat-at-rekh''-o\\@', 'from \\\\2596\\\\ and \\\\5143\\\\', '', 'v', 'run down 1', 1, ' 1) to run down, hasten down\r\n'),
(2702, 'kataphero', '\\~kataferw\\~ kataphero', '\\@kat-af-er''-o\\@', 'from \\\\2596\\\\ and \\\\5342\\\\ (including its alternate)', '', 'v', 'fall 1, sink down 1, give 1', 3, ' 1) to bear down, bring down, cast down\r\n    1a) to cast a pebble or calculus into the urn\r\n        1a1) to give one''s vote, to approve\r\n    1b) to be born down, to sink, (from the window to the pavement)\r\n    1c) metaph.\r\n        1c1) to be weighted down by, overcome, carried away\r\n        1c2) to sink into sleep, drop asleep\r\n'),
(2703, 'katapheugo', '\\~katafeugw\\~ katapheugo', '\\@kat-af-yoo''-go\\@', 'from \\\\2596\\\\ and \\\\5343\\\\', '', 'v', 'flee 2', 2, ' 1) to flee away, flee for refuge\r\n'),
(2704, 'katphtheiro', '\\~katafyeirw\\~ katphtheiro', '\\@kat-af-thi''-ro\\@', 'from \\\\2596\\\\ and \\\\5351\\\\', 'TDNT - 9:93,1259', 'v', 'corrupt 1, utterly perish 1', 2, ' 1) to corrupt, deprave\r\n    1a) corrupted in mind\r\n 2) to destroy\r\n    2a) to be destroyed, to perish\r\n'),
(2705, 'kataphileo', '\\~katafilew\\~ kataphileo', '\\@kat-af-ee-leh''-o\\@', 'from \\\\2596\\\\ and \\\\5368\\\\', 'TDNT - 9:114,1262', 'v', 'kiss 6', 6, ' 1) to kiss much, kiss again and again, kiss tenderly\r\n'),
(2706, 'kataphroneo', '\\~katafronew\\~ kataphroneo', '\\@kat-af-ron-eh''-o\\@', 'from \\\\2596\\\\ and \\\\5426\\\\', 'TDNT - 3:631,421', 'v', 'despise 9', 9, ' 1) to contemn, despise, disdain, think little or nothing of\r\n'),
