INSERT INTO `hebrew` (`id`, `word_id`, `word`, `pron`, `origin`, `source`, `kind`, `av`, `count`, `definition`) VALUES
(0, NULL, '', '', '', '', '', '', 0, '\r\n The original word in the Greek or Hebrew is translated by more\r\n than one word in the English. The English translation is\r\n separated by one or more other words from the original.\r\n\r\n e.g. \\\\#Mt 15:23\\\\\r\n    Strong''s No. 630            English\r\n    to send away                send 630 0 her 846 away 630\r\n\r\n Here the verb "send away" is split by the word "her". The zero\r\n means the verb only occurs once in this passage not twice.\r\n Sometimes five or six words separate a word.\r\n\r\n Some exceptions are in \\\\#Jer 51:3\\\\ where the zero in\r\n "bendeth 1869 0 let the archer 1869 8802 bend 1869 8799"\r\n connects "bendeth" with "bend" not "archer".\r\n See also \\\\#Nu 16:13, 22:17, 2Sa 12:14\\\\.\r\n\r\n'),
(1, '''ab', '\\^ba\\^ ''ab', '\\\\awb\\\\', 'a root', 'TWOT - 4a', 'n m', 'father 1205, chief 2, families 2, desire 1, fatherless + \\\\0369\\\\ 1, forefathers + \\\\07223\\\\ 1, patrimony 1, prince 1, principal 1', 1215, ' 1) father of an individual\r\n 2) of God as father of his people\r\n 3) head or founder of a household, group, family, or clan\r\n 4) ancestor\r\n    4a) grandfather, forefathers -- of person\r\n    4b) of people\r\n 5) originator or patron of a class, profession, or art\r\n 6) of producer, generator (fig.)\r\n 7) of benevolence and protection (fig.)\r\n 8) term of respect and honour\r\n 9) ruler or chief (spec.)\r\n'),
(2, '''ab', '\\^ba\\^ ''ab (Aramaic)', '\\\\ab\\\\', 'corresponding to \\\\01\\\\, Greek \\\\5\\\\ \\~Abba\\~ and \\\\912\\\\ \\~barabbav\\~', 'TWOT - 2553', 'n m', 'father 9', 9, ' 1) father\r\n'),
(3, '''eb', '\\^ba\\^ ''eb', '\\\\abe\\\\', 'from the same as \\\\024\\\\', 'TWOT - 1a', 'n m', 'greenness 1, fruits 1', 2, ' 1) freshness, fresh green, green shoots, or greenery\r\n'),
(4, '''eb', '\\^ba\\^ ''eb (Aramaic)', '\\\\abe\\\\', 'corresponding to \\\\03\\\\', 'TWOT - 2554', 'n m', 'fruit 3', 3, ' 1) fruit, fresh, young, greening\r\n'),
(5, '''Abagtha''', '\\^atgba\\^ ''Abagtha''', '\\\\ab-ag-thaw''\\\\', 'of foreign origin', '', 'n pr m', 'Abagtha 1', 1, ' Abagtha = "God-given"\r\n\r\n 1) one of the seven eunuchs in the Persian court of Ahasuerus\r\n'),
(6, '''abad', '\\^dba\\^ ''abad', '\\\\aw-bad''\\\\', 'a primitive root', 'TWOT - 2', 'v', 'perish 98, destroy 62, lose 10, fail 2, surely 2, utterly 2, broken 1, destruction 1, escape 1, flee 1, spendeth 1, take 1, undone 1, void 1', 184, ' 1) perish, vanish, go astray, be destroyed\r\n    1a) (Qal)\r\n        1a1) perish, die, be exterminated\r\n        1a2) perish, vanish (fig.)\r\n        1a3) be lost, strayed\r\n    1b) (Piel)\r\n        1b1) to destroy, kill, cause to perish, to give up\r\n             (as lost), exterminate\r\n        1b2) to blot out, do away with, cause to vanish, (fig.)\r\n        1b3) cause to stray, lose\r\n    1c) (Hiphil)\r\n        1c1) to destroy, put to death\r\n             1c1a) of divine judgment\r\n        1c2) object name of kings (fig.)\r\n'),
(7, '''abad', '\\^dba\\^ ''abad (Aramaic)', '\\\\ab-ad''\\\\', 'corresponding to \\\\06\\\\', 'TWOT - 2555', 'v', 'to destroy 5, to perish 2', 7, ' 1) to perish, vanish\r\n    1a) (P''al) shall perish\r\n    1b) (Aphel) destroy\r\n    1c) (Hophal) be destroyed\r\n'),
(8, '''obed', '\\^dba\\^ ''obed', '\\\\o-bade''\\\\', 'active of participle of \\\\06\\\\', 'TWOT - 2a', 'n m', 'perish 2', 2, ' 1) destruction\r\n'),
(9, '''abedah', '\\^hdba\\^ ''abedah', '\\\\ab-ay-daw''\\\\', 'from \\\\06\\\\', 'TWOT - 2b', 'n f', 'lost thing 3, that which was lost 1', 4, ' 1) a lost thing, something lost\r\n'),
(10, '''abaddoh', '\\^hdba\\^ ''abaddoh', '\\\\ab-ad-do''\\\\', 'the same as \\\\09\\\\, incorrectly written for \\\\011\\\\', 'TWOT - 2b', 'n f', 'destruction 1', 1, ' 1) a lost thing, something lost, a perishing\r\n'),
(11, '''abaddown', '\\^Ndba\\^ ''abaddown', '\\\\ab-ad-done''\\\\', 'intensive from \\\\06\\\\, Greek \\\\3\\\\ \\~Abaddwn\\~', 'TWOT - 2d', 'n pr loc', 'destruction 6', 6, ' 1) place of destruction, destruction, ruin, Abaddon\r\n'),
(12, '''abdan', '\\^Ndba\\^ ''abdan', '\\\\ab-dawn''\\\\', 'from \\\\06\\\\', 'TWOT - 2c', 'n m', 'destruction 1', 1, ' 1) destruction\r\n'),
(13, '''obdan', '\\^Nbda\\^ ''obdan', '\\\\ob-dawn''\\\\', 'from \\\\06\\\\', 'TWOT - 2c', 'n m', 'destruction 1', 1, ' 1) destruction\r\n'),
(14, '''abah', '\\^hba\\^ ''abah', '\\\\aw-baw''\\\\', 'a primitive root', 'TWOT - 3', 'v', 'would 42, will 4, willing 4, consent 3, rest content 1', 54, ' 1) to be willing, consent\r\n    1a) (Qal)\r\n        1a1) to be willing\r\n        1a2) to consent, yield to, accept\r\n        1a3) to desire\r\n'),
(15, '''abeh', '\\^hba\\^ ''abeh', '\\\\aw-beh''\\\\', 'from \\\\014\\\\', 'TWOT - 3', 'n f', 'desire 1', 1, ' 1) entreat, longing, desire\r\n'),
(16, '''ebeh', '\\^hba\\^ ''ebeh', '\\\\ay-beh''\\\\', 'from \\\\014\\\\ (in the sense of bending toward)', 'TWOT - 3c', 'n m', 'swift 1', 1, ' 1) reed, papyrus\r\n ++++\r\n Probably ships made of reeds or papyrus were the clipper sailing\r\n ships of those days because of their speed.\r\n'),
(17, '''abowy', '\\^ywba\\^ ''abowy', '\\\\ab-o''ee\\\\', 'from \\\\014\\\\ (in the sense of desiring)', 'TWOT - 3d', 'interj', 'sorrow 1', 1, ' 1) Oh!, Woe!, (exclamation of pain -- indicates desire or uneasiness)\r\n'),
(18, '''ebuwc', '\\^owba\\^ ''ebuwc', '\\\\ay-booce''\\\\', 'from \\\\075\\\\', 'TWOT - 10a', 'n m', 'crib 3', 3, ' 1) crib, manger, feeding trough\r\n'),
(19, '''ibchah', '\\^hxba\\^ ''ibchah', '\\\\ib-khaw''\\\\', 'from an unused root (apparently meaning to turn)', 'TWOT - 786b', 'n f', 'point (of the sword) 1', 1, ' 1) slaughter, flesh, meat, slaughtered meat\r\n'),
(20, '''abattiyach', '\\^Myxyjba\\^ ''abattiyach', '\\\\ab-at-tee''-akh\\\\', 'of uncertain derivation', 'TWOT - 234a', 'n m', 'melons 1', 1, ' 1) watermelon, Egyptian fruit\r\n'),
(21, '''Abiy', '\\^yba\\^ ''Abiy', '\\\\ab-ee''\\\\', 'from \\\\01\\\\', '', 'n pr f', 'Abi 1', 1, ' Abi = "my father"\r\n\r\n 1) mother of Hezekiah (cf \\\\029\\\\)\r\n'),
(22, '''Abiy''el', '\\^layba\\^ ''Abiy''el', '\\\\ab-ee-ale''\\\\', 'from \\\\01\\\\ and \\\\0410\\\\', '', 'n pr m', 'Abiel 3', 3, ' Abiel = "El (God) is (my) father"\r\n\r\n 1) Saul''s grandfather\r\n'),
(23, '''Abiy''acaph', '\\^Poayba\\^ ''Abiy''acaph', '\\\\ab-ee-aw-sawf''\\\\', 'from \\\\01\\\\ and \\\\0622\\\\', '', 'n pr m', 'Abiasaph 1', 1, ' Abiasaph = "my father has gathered"\r\n\r\n 1) son (descendant) of Korah\r\n'),
(24, '''abiyb', '\\^byba\\^ ''abiyb', '\\\\aw-beeb''\\\\', 'from an unused root (meaning to be tender)', 'TWOT - 1b', 'n m', 'Abib 6, in the ear 1, green ears of corn 1', 8, ' 1) fresh, young barley ears, barley\r\n 2) month of ear-forming, of greening of crop, of growing green\r\n    Abib, month of exodus and passover (March or April)\r\n'),
(25, '''Abiy', '\\^Nwebg yba\\^ ''Abiy Gib`own', '\\\\ab-ee'' ghib-one''\\\\', 'from \\\\01\\\\ and \\\\01391\\\\', '', 'n pr m', 'father of Gibeon 2', 2, ' Abi Gibon = "father of Gibeon"\r\n\r\n 1) site of great Bamah, location of the tabernacle of the Lord\r\n    in the high place\r\n'),
(26, '''Abiygayil', '\\^lygyba\\^ ''Abiygayil or shorter \\^lgyba\\^ ''Abiygal', '\\\\ab-ee-gah''-yil\\\\ \\\\ab-ee-gal''\\\\', 'from \\\\01\\\\ and \\\\01524\\\\', '', 'n pr f', 'Abigail 17', 17, ' Abigail = "my father is joy"\r\n\r\n 1) wife of Nabal, then of David\r\n 2) sister of David\r\n'),
(27, '''Abiydan', '\\^Ndyba\\^ ''Abiydan', '\\\\ab-ee-dawn''\\\\', 'from \\\\01\\\\ and \\\\01777\\\\', '', 'n pr m', 'Abidan 5', 5, ' Abidan = "my father is judge"\r\n\r\n 1) a prince (ruler) of Benjamin\r\n'),
(28, '''Abiyda`', '\\^edyba\\^ ''Abiyda`', '\\\\ab-ee-daw''\\\\', 'from \\\\01\\\\ and \\\\03045\\\\', '', 'n pr m', 'Abida 2', 2, ' Abida or Abidah = "my father knows"\r\n\r\n 1) fourth son of Midian and grandson of Abraham by his wife\r\n    Keturah (after Sarah died)\r\n'),
(29, '''Abiyah', '\\^hyba\\^ ''Abiyah or prolonged \\^whyba\\^ ''Abiyahuw', '\\\\ab-ee-yaw''\\\\ \\\\ab-ee-yaw''-hoo\\\\', 'from \\\\01\\\\ and \\\\03050\\\\, Greek \\\\7\\\\ \\~Abia\\~', '', 'n pr m', 'Abijah 20, Abiah 4, Abia 1', 25, ' Abia or Abiah or Abijah = "Jehovah is (my) father"\r\n\r\n 1) king of Judah, son and successor of Rehoboam\r\n 2) second son of Samuel\r\n 3) son of Jeroboam the first, king of Israel\r\n 4) son of Becher, a Benjamite\r\n 5) head of a priestly house (one of the 24 Levite groups)\r\n 6) head of a priestly house (after the exile)\r\n 7) wife of Hezron\r\n 8) mother of Hezekiah (cf \\\\021\\\\)\r\n'),
(30, '''Abiyhuw''', '\\^whyba\\^ ''Abiyhuw''', '\\\\ab-ee-hoo''\\\\', 'from \\\\01\\\\ and \\\\01931\\\\', '', 'n pr m', 'Abihu 12', 12, ' Abihu = "he is (my) father"\r\n\r\n 1) a son of Aaron destroyed for sacrificing strange fire to God\r\n'),
(31, '''Abiyhuwd', '\\^dwhyba\\^ ''Abiyhuwd', '\\\\ab-ee-hood''\\\\', 'from \\\\01\\\\ and \\\\01935\\\\, Greek \\\\10\\\\ \\~Abioud\\~', '', 'n pr m', 'Ahihud 1', 1, ' Ahihud = "my father is majesty"\r\n\r\n 1) son of Bela, a Benjamite\r\n'),
(32, '''Abiyhayil', '\\^lyhyba\\^ ''Abiyhayil or (more correctly) \\^lyxyba\\^ ''Abiychayil', '\\\\ab-ee-hah''-yil\\\\ \\\\ab-ee-khah''-yil\\\\', 'from \\\\01\\\\ and \\\\02428\\\\', '', 'n pr m', 'Abihail 6', 6, ' Abihail = "my father is might"\r\n\r\n 1) a Levite in Moses''s time\r\n 2) a Gadite\r\n 3) father of Esther\r\n 4) wife of Abishur\r\n 5) wife of Rehoboam\r\n'),
(33, '''Abiy', '\\^yrzeh yba\\^ ''Abiy ha-`Ezriy', '\\\\ab-ee''-haw-ez-ree''\\\\', 'from \\\\044\\\\ with the article inserted', '', 'n pr m', 'Abiezrite 3', 3, ' Abiezrite = "my father is help"\r\n\r\n 1) one of the family of Abiezer, a descendant of Joseph''s son, Manasseh\r\n'),
(34, '''ebyown', '\\^Nwyba\\^ ''ebyown', '\\\\eb-yone''\\\\', 'from \\\\014\\\\, in the sense of want (especially in feeling)', 'TWOT - 3a', 'm adj', 'needy 35, poor 24, beggar 1, poor man 1', 61, ' 1) in want, needy, chiefly poor, needy person\r\n 2) subject to oppression and abuse\r\n 3) needing help, deliverance from trouble, especially as delivered by God\r\n 4) general reference to lowest class\r\n'),
(35, '''abiyownah', '\\^hnwyba\\^ ''abiyownah', '\\\\ab-ee-yo-naw''\\\\', 'from \\\\014\\\\', 'TWOT - 3b', 'n f', 'desire 1', 1, ' 1) caper-berry - of stimulating desire\r\n'),
(36, '''Abiytuwb', '\\^bwjyba\\^ ''Abiytuwb', '\\\\ab-ee-toob''\\\\', 'from \\\\01\\\\ and \\\\02898\\\\', '', 'n pr m', 'Abitub 1', 1, ' Abitub = "my father is good(ness)"\r\n\r\n 1) son of Shaharaim, a Benjamite\r\n'),
(37, '''Abiytal', '\\^ljyba\\^ ''Abiytal', '\\\\ab-ee-tal''\\\\', 'from \\\\01\\\\ and \\\\02919\\\\', '', 'n pr f', 'Abital 2', 2, ' Abital = "my father is (the) dew"\r\n\r\n 1) a wife of David\r\n'),
(38, '''Abiyam', '\\^Myba\\^ ''Abiyam', '\\\\ab-ee-yawm''\\\\', 'from \\\\01\\\\ and \\\\03220\\\\', '', 'n pr m', 'Abijam 5', 5, ' Abijam = "my father is the sea" or "Yah(u) is (my) father"\r\n\r\n 1) king of Judah, son and successor of Rehoboam\r\n'),
(39, '''Abiyma''el', '\\^lamyba\\^ ''Abiyma''el', '\\\\ab-ee-maw-ale''\\\\', 'from \\\\01\\\\ and an elsewhere unused (probably foreign) word', '', 'n pr m', 'Abimael 2', 2, ' Abimael = "my father is El (God)"\r\n\r\n 1) son of Joktan, descendant of Shem\r\n'),
(40, '''Abiymelek', '\\^Klmyba\\^ ''Abiymelek', '\\\\ab-ee-mel''-ek\\\\', 'from \\\\01\\\\ and \\\\04428\\\\', '', 'n pr m', 'Abimelech 67', 67, ' Abimelech = "Melek is father" or "my father is king"\r\n\r\n 1) king of Gerar in Abraham''s time\r\n 2) king of Gath in David''s time; maybe title of Philistine kings\r\n 3) son of Gideon by a concubine\r\n 4) priest, son of Abiathar\r\n'),
(41, '''Abiynadab', '\\^bdnyba\\^ ''Abiynadab', '\\\\ab-ee-naw-dawb''\\\\', 'from \\\\01\\\\ and \\\\05068\\\\', '', 'n pr m', 'Abinadab 12', 12, ' Abinadab = "my father is noble" or "my father is willing"\r\n\r\n 1) a man of Gibeah who sheltered the ark\r\n 2) second son of Jesse, David''s older brother\r\n 3) a son of Saul, slain with him by the Philistines\r\n'),
(42, '''Abiyno`am', '\\^Menyba\\^ ''Abiyno`am', '\\\\ab-ee-no''-am\\\\', 'from \\\\01\\\\ and \\\\05278\\\\', '', 'n pr m', 'Abinoam 4', 4, ' Abinoam = "my father is delight"\r\n\r\n 1) father of Barak, descendant of Naphtali\r\n'),
(43, '''Ebyacaph', '\\^Poyba\\^ ''Ebyacaph', '\\\\eb-yaw-sawf''\\\\', 'contracted from \\\\023\\\\', '', 'n pr m', 'Ebiasaph 3', 3, ' Ebiasaph = "my father has gathered"\r\n\r\n 1) a son or descendant of Korah\r\n'),
(44, '''Abiy`ezer', '\\^rzeyba\\^ ''Abiy`ezer', '\\\\ab-ee-ay''-zer\\\\', 'from \\\\01\\\\ and \\\\05829\\\\', '', 'n pr m', 'Abiezer 7', 7, ' Abiezer = "my father is help"\r\n\r\n 1) a Manassite, called "son" of Gilead, also son of Gilead''s sister\r\n 2) a Benjamite, warrior of David\r\n'),
(45, '''Abiy-`albown', '\\^Nwble-yba\\^ ''Abiy-`albown', '\\\\ab-ee-al-bone''\\\\', 'from \\\\01\\\\ and an unused root of uncertain derivation', '', 'n pr m', 'Abialbon 1', 1, ' Abialbon = "El (God) is my father"\r\n\r\n 1) one of David''s mighty men (hero)\r\n'),
(46, '''abiyr', '\\^ryba\\^ ''abiyr', '\\\\aw-beer''\\\\', 'from \\\\082\\\\', 'TWOT - 13c', 'adj m', 'might (One) 6', 6, ' 1) strong, mighty - used only to describe God\r\n 2) the Strong - old name for God (poetic)\r\n'),
(47, '''abbiyr', '\\^ryba\\^ ''abbiyr', '\\\\ab-beer''\\\\', 'from \\\\046\\\\', 'TWOT - 13d', 'adj m', 'bulls 4, strong (ones) 4, mighty 3, stouthearted 2, valiant 2, angels 1, chiefest 1', 17, ' 1) mighty, valiant\r\n    1a) of men\r\n    1b) of angels\r\n    1c) of animals\r\n    1d) (metaph)\r\n        1d1) of enemies\r\n        1d2) of princes\r\n        1d3) of sacrificial objects\r\n    1e) obstinate (fig.)\r\n'),
(48, '''Abiyram', '\\^Mryba\\^ ''Abiyram', '\\\\ab-ee-rawm''\\\\', 'from \\\\01\\\\ and \\\\07311\\\\', '', 'n pr m', 'Abiram 11', 11, ' Abiram = "my father is exalted" or "(the) Exalted One is (my) father"\r\n\r\n 1) a Reubenite, son of Eliab in exodus\r\n 2) son of Hiel the Bethelite who worked to rebuild Jericho\r\n'),
(49, '''Abiyshag', '\\^gvyba\\^ ''Abiyshag', '\\\\ab-ee-shag''\\\\', 'from \\\\01\\\\ and \\\\07686\\\\', '', 'n pr f', 'Abishag 5', 5, ' Abishag = "my father is a wanderer"\r\n\r\n 1) David''s beautiful young nurse\r\n'),
(50, '''Abiyshuwae', '\\^ewvyba\\^ ''Abiyshuwae', '\\\\ab-ee-shoo''-ah\\\\', 'from \\\\01\\\\ and \\\\07771\\\\', '', 'n pr m', 'Abishua 5', 5, ' Abishua = "my father is rescue (safety), or is opulence"\r\n\r\n 1) son of Phinehas, grandson of Aaron\r\n'),
(51, '''Abiyshuwr', '\\^rwvyba\\^ ''Abiyshuwr', '\\\\ab-ee-shoor''\\\\', 'from \\\\01\\\\ and \\\\07791\\\\', '', 'n pr m', 'Abishur 2', 2, ' Abishur = "my father is a wall"\r\n\r\n 1) son of Shammai, descendant of Judah\r\n'),
(52, '''Abiyshay', '\\^yvyba\\^ ''Abiyshay or (shorter) \\^yvba\\^ ''Abshay', '\\\\ab-ee-shah''ee\\\\ \\\\ab-shah''ee\\\\', 'from \\\\01\\\\ and \\\\07862\\\\', '', 'n pr m', 'Abishai 25', 25, ' Abishai = "my father is Jesse" or "my father is a gift"\r\n\r\n 1) grandson of Jesse, nephew of David via his sister Zeruiah,\r\n    brother of Joab\r\n'),
(53, '''Abiyshalowm', '\\^Mwlvyba\\^ ''Abiyshalowm or (shortened) \\^Mwlvba\\^ ''Abshalowm', '\\\\ab-ee-shaw-lome''\\\\ \\\\ab-shaw-lome''\\\\', 'from \\\\01\\\\ and \\\\07965\\\\', '', 'n pr m', 'Absalom 109, Abishalom 2', 111, ' Absalom or Abishalom = "my father is peace"\r\n\r\n 1) father-in-law of Rehoboam\r\n 2) third son of David, killer of first-born son Amnon, also\r\n    leader of revolt against his father - David\r\n'),
(54, '''Ebyathar', '\\^rtyba\\^ ''Ebyathar', '\\\\ab-yaw-thawr''\\\\', 'contracted from \\\\01\\\\ and \\\\03498\\\\, Greek \\\\8\\\\ \\~Abiayar\\~', '', 'n pr m', 'Abiathar 30', 30, ' Abiathar = "my father is great"\r\n\r\n 1) priest, son of Ahitub (Ahimelech), faithful to David, but\r\n    later rebelled with Adonijah\r\n'),
(55, '''abak', '\\^Kba\\^ ''abak', '\\\\aw-bak''\\\\', 'a primitive root', 'TWOT - 5', 'v', 'mount up 1', 1, ' 1) to roll, turn\r\n   1a) (Hithpael) to roll, roll up, billow\r\n'),
(56, '''abal', '\\^lba\\^ ''abal', '\\\\aw-bal''\\\\', 'a primitive root', 'TWOT - 6', 'v', 'mourn 36, lament 3', 39, ' 1) to mourn, lament\r\n    1a) (Qal) to mourn, lament\r\n        1a1) of humans\r\n        1a2) of inanimate objects (fig.)\r\n             1a2a) of gates\r\n             1a2b) of land\r\n    1b) (Hiphil)\r\n        1b1) to mourn, cause to mourn (fig.)\r\n    1c) (Hithpael)\r\n        1c1) to mourn\r\n        1c2) play the mourner\r\n'),
(57, '''abel', '\\^lba\\^ ''abel', '\\\\aw-bale''\\\\', 'from \\\\056\\\\', 'TWOT - 6b', 'adj', 'mourn 8', 8, ' 1) mourning\r\n    1a) for the dead\r\n    1b) because of calamity\r\n    1c) of rites of mourning\r\n 2) mourner (subst.)\r\n    2a) for the dead\r\n    2b) for calamity\r\n'),
(58, '''abel', '\\^lba\\^ ''abel', '\\\\aw-bale''\\\\', 'from an unused root (meaning to be grassy), Greek \\\\9\\\\ \\~Abilhnh\\~', 'TWOT - 7a', 'n f', 'plain 1', 1, ' 1) meadow (from verb - to grow green, to withstand)\r\n'),
(59, '''Abel', '\\^lba\\^ ''Abel', '\\\\aw-bale''\\\\', 'from \\\\058\\\\', '', 'n pr loc', 'Abel 4', 4, ' 1) city in northern Israel near Bethmaachah\r\n 2) the place where the ark rested in the field of Joshua at Bethshemesh\r\n'),
(60, '''ebel', '\\^lba\\^ ''ebel', '\\\\ay''-bel\\\\', 'from \\\\056\\\\', 'TWOT - 6a', 'n m', 'mourning 24', 24, ' 1) mourning\r\n    1a) for the dead\r\n    1b) for rites of mourning (metaph)\r\n    1c) mourning garb\r\n    1d) period of mourning\r\n'),
(61, '''abal', '\\^lba\\^ ''abal', '\\\\ab-awl''\\\\', 'apparently from \\\\056\\\\ through the idea of negation', 'TWOT - 8', 'adv', 'but 4, verily 3, indeed 2, nevertheless 2', 11, ' 1) truly, verily, surely\r\n 2) but, however, howbeit\r\n 3) contrariwise, nay rather (neg.)\r\n'),
(62, '''Abel', '\\^hkem-tyb lba\\^ ''Abel Beyth-Ma`akah', '\\\\aw-bale'' bayth ma-a-kaw''\\\\', 'from \\\\058\\\\ and \\\\01004\\\\ and \\\\04601\\\\', '', 'n pr loc', 'Abelbethmaachah 2', 2, ' Abel Beth Maachah = "meadow of the house of Maachah"\r\n\r\n 1) city in northern Israel near Beth Maachah\r\n'),
(63, '''Abel', '\\^Myjvh lba\\^ ''Abel hash-Shittiym', '\\\\aw-bale'' hash-shit-teem''\\\\', 'from \\\\058\\\\ and the plural of \\\\07848\\\\, with the article inserted', '', 'n pr loc', 'Abelshittim 1', 1, ' Abel Shittim = "meadow of acacias"\r\n\r\n 1) place in lowlands of Moab\r\n'),
(64, '''Abel', '\\^Mymrk lba\\^ ''Abel K@ramiym', '\\\\aw-bale'' ker-aw-meem''\\\\', 'from \\\\058\\\\ and the plural of \\\\03754\\\\', '', 'n pr loc', 'plain of the vineyards 1', 1, ' Abel Keramim = "meadow of the vineyards"\r\n\r\n 1) a place in Ammon\r\n'),
(65, '''Abel', '\\^lhwxm lba\\^ ''Abel M@chowlah', '\\\\aw-bale'' mekh-o-law''\\\\', 'from \\\\058\\\\ and \\\\04246\\\\', '', 'n pr loc', 'Abelmeholah 3', 3, ' Abel Meholah = "meadow of dancing"\r\n\r\n 1) a city of Issachar, birthplace of Elisha\r\n'),
(66, '''Abel', '\\^Mym lba\\^ ''Abel Mayim', '\\\\aw-bale'' mah''-yim\\\\', 'from \\\\058\\\\ and \\\\04325\\\\', '', 'n pr loc', 'Abelmaim 1', 1, ' Abel Maim = "meadow of waters"\r\n\r\n 1) a city in northern Israel (perhaps Abel Beth Maachah)\r\n'),
