(493, '''Elna`am', '\\^Menla\\^ ''Elna`am', '\\\\el-nah''-am\\\\', 'from \\\\0410\\\\ and \\\\05276\\\\', '', 'n pr m', 'Elnaam 1', 1, ' Elnaam = "God is delight" or "God is pleasantness"\r\n\r\n 1) the father of two of David''s mighty warriors\r\n'),
(494, '''Elnathan', '\\^Ntnla\\^ ''Elnathan', '\\\\el-naw-thawn''\\\\', 'from \\\\0410\\\\ and \\\\05414\\\\', '', 'n pr m', 'Elnathan 7', 7, ' Elnathan = "God has given"\r\n\r\n 1) king Jehoiachin''s maternal grandfather\r\n 2) three chief men in Ezra''s time\r\n 3) son of Achbor, a military commander under Jehoiakim\r\n'),
(495, '''Ellacar', '\\^rola\\^ ''Ellacar', '\\\\el-law-sawr''\\\\', 'probably of foreign derivation', '', 'n pr loc', 'Ellasar 2', 2, ' Ellasar = "God is chastener"\r\n\r\n 1) a town in Babylonia, c. 28 miles (50 km) E of Ur\r\n'),
