(511, '''Elqanah', '\\^hnqla\\^ ''Elqanah', '\\\\el-kaw-naw''\\\\', 'from \\\\0410\\\\ and \\\\07069\\\\', '', 'n pr m', 'Elkanah 21', 21, ' Elkanah = "God has possessed" or "God has created"\r\n\r\n 1) Samuel''s father\r\n 2) a ruler in Jerusalem in the time of king Ahaz\r\n 3) one of David''s mighty warriors\r\n 4) son of Korah\r\n 5) several Levites\r\n'),
(512, '''Elqoshiy', '\\^yvqla\\^ ''Elqoshiy', '\\\\el-ko-shee''\\\\', 'patrial from a name of uncertain derivation', '', 'adj', 'Elkoshite 1', 1, ' Elkoshite = "God the ensnarer"\r\n\r\n 1) a native and/or descendant of Elkosh (location unknown)\r\n'),
