(526, '''Amown', '\\^Nwma\\^ ''Amown', '\\\\aw-mone''\\\\', 'the same as \\\\0525\\\\, Greek \\\\300\\\\ \\~Amwn\\~', '', 'n pr m', 'Amon 17', 17, ' Amon = "skilled workman" or "master workman"\r\n\r\n 1) a king of Judah, son of Manasseh\r\n 2) a governor of Samaria\r\n 3) a descendant of a servant of Solomon\r\n'),
(527, '''amown', '\\^Nwma\\^ ''amown', '\\\\aw-mone''\\\\', 'a variation for \\\\01995\\\\', 'TWOT - 116L', 'n m', 'multitude 2, populous 1', 3, ' 1) artificer, architect, master workman\r\n 2) (CLBL) throng, multitude\r\n'),
(528, '''Amown', '\\^Nwma\\^ ''Amown', '\\\\aw-mone''\\\\', 'of Egyptian derivation', '', 'n pr m', 'not translated 2', 2, ' Amon or Amun = "to nourish: to be faithful"\r\n\r\n 1) an Egyptian god, originally the local god of Thebes, later head of\r\n    the Egyptian pantheon\r\n'),
