(779, '''arar', '\\^rra\\^ ''arar', '\\\\aw-rar''\\\\', 'a primitive root', 'TWOT - 168', 'v', 'curse 62, bitterly 1', 63, ' 1) to curse\r\n    1a) (Qal)\r\n        1a1) to curse\r\n        1a2) cursed be he (participle used as in curses)\r\n    1b) (Niphal) to be cursed, cursed\r\n    1c) (Piel) to curse, lay under a curse, put a curse on\r\n    1d) (Hophal) to be made a curse, be cursed\r\n'),
(780, '''Ararat', '\\^jrra\\^ ''Ararat', '\\\\ar-aw-rat''\\\\', 'of foreign origin', 'TWOT - 169', 'n pr loc', 'Ararat 2, Armenia 2', 4, ' Ararat = "the curse reversed: precipitation of curse"\r\n\r\n 1) a mountainous region of eastern Armenia, between the river Araxes and\r\n    the lakes Van and Oroomiah, the site where Noah''s ark came to rest\r\n 2) (TWOT) the mountain where Noah''s ark came to rest\r\n'),
(781, '''aras', '\\^sra\\^ ''aras', '\\\\aw-ras''\\\\', 'a primitive root', 'TWOT - 170', 'v', 'betroth 10, espouse 1', 11, ' 1) to betroth, engage\r\n    1a) (Piel) to betroth (man or woman)\r\n    1b) (Pual) to be betrothed\r\n'),
