(932, 'Bohan', '\\^Nhb\\^ Bohan', '\\\\bo''han\\\\', 'an orthographical variation of \\\\0931\\\\', '', 'n pr m', 'Bohan 2', 2, ' Bohan = "thumb"\r\n\r\n 1) a Reubenite who set a boundary stone between Judah and Benjamin\r\n 2) (BDB) a mark of division between Judah and Benjamin\r\n'),
(933, 'bohaq', '\\^qhb\\^ bohaq', '\\\\bo''-hak\\\\', 'from an unused root meaning to be pale', 'TWOT - 210a', 'n m', 'freckled spot 1', 1, ' 1) a harmless eruption of the skin, skin spot\r\n'),
(934, 'bohereth', '\\^trhb\\^ bohereth', '\\\\bo-heh''-reth\\\\', 'active participle of the same as \\\\0925\\\\', 'TWOT - 211a', 'n f', 'bright spot 13', 13, ' 1) white patch of skin, brightness, bright spot (on skin)\r\n 2) (CLBL) bright spot, scar, blister, boil\r\n    2a) resulting from fire\r\n    2b) resulting from inflammation of the skin\r\n    2c) possible sign of leprosy\r\n'),
(935, 'bow''', '\\^awb\\^ bow''', '\\\\bo\\\\', 'a primitive root', 'TWOT - 212', 'v', 'come 1435, bring 487, ... in 233, enter 125, go 123, carry 17, ...down 23, pass 13, ...out 12, misc 109', 2577, ' 1) to go in, enter, come, go, come in\r\n    1a) (Qal)\r\n        1a1) to enter, come in\r\n        1a2) to come\r\n             1a2a) to come with\r\n             1a2b) to come upon, fall or light upon, attack (enemy)\r\n             1a2c) to come to pass\r\n        1a3) to attain to\r\n        1a4) to be enumerated\r\n        1a5) to go\r\n    1b) (Hiphil)\r\n        1b1) to lead in\r\n        1b2) to carry in\r\n        1b3) to bring in, cause to come in, gather, cause to come,\r\n             bring near, bring against, bring upon\r\n        1b4) to bring to pass\r\n    1c) (Hophal)\r\n        1c1) to be brought, brought in\r\n        1c2) to be introduced, be put\r\n'),
(936, 'buwz', '\\^zwb\\^ buwz', '\\\\booz\\\\', 'a primitive root', 'TWOT - 213', 'v', 'despise 10, contemned 1, utterly (inf. for emphasis) 1', 12, ' 1) to despise, hold in contempt, hold as insignificant\r\n    1a) (Qal) to despise, show despite toward\r\n'),
(937, 'buwz', '\\^zwb\\^ buwz', '\\\\booz\\\\', 'from \\\\0936\\\\', 'TWOT - 213a', 'n m', 'contempt 7, despised 2, contemptuously 1, shamed 1', 11, ' 1) contempt\r\n    1a) springing from evil\r\n    1b) springing from prosperity\r\n    1c) springing from judgment\r\n'),
(938, 'Buwz', '\\^zwb\\^ Buwz', '\\\\booz\\\\', 'the same as \\\\0937\\\\', '', 'n pr m', 'Buz 3', 3, ' Buz = "contempt"\r\n\r\n 1) second son of Nahor, Abraham''s nephew\r\n 2) a Gadite\r\n'),
