(1292, 'Barak''el', '\\^lakrb\\^ Barak''el', '\\\\baw-rak-ale''\\\\', 'from \\\\01288\\\\ and \\\\0410\\\\', '', 'n pr m', 'Barachel 2', 2, ' Barachel = "God blesses"\r\n\r\n 1) father of Elihu\r\n'),
(1293, 'B@rakah', '\\^hrkb\\^ B@rakah', '\\\\ber-aw-kaw''\\\\', 'from \\\\01288\\\\', '', 'n f', 'blessing 61, blessed 3, present 3, liberal 1, pools 1', 69, ' 1) blessing\r\n 2) (source of) blessing\r\n 3) blessing, prosperity\r\n 4) blessing, praise of God\r\n 5) a gift, present\r\n 6) treaty of peace\r\n'),
(1294, 'B@rakah', '\\^hrkb\\^ B@rakah', '\\\\ber-aw-kaw''\\\\', 'the same as \\\\01293\\\\', '', '', 'Berachah 3', 3, ' Berachah = "blessing"\r\n\r\n n pr m\r\n 1) a Benjamite, one of David''s warriors\r\n\r\n n pr loc\r\n 2) a valley in the wilderness near Tekoa where Jehoshaphat and his\r\n    people assembled to bless Jehovah after the overthrow of the hosts\r\n    of the Moabites\r\n'),
(1295, 'b@rekah', '\\^hkrb\\^ b@rekah', '\\\\ber-ay-kaw''\\\\', 'from \\\\01288\\\\', 'TWOT - 285c', 'n f', 'pool 16, fishpools 1', 17, ' 1) pool, pond\r\n'),
(1296, 'Berekyah', '\\^hykrb\\^ Berekyah or  \\^whykrb\\^ Berekyahuw', '\\\\beh-rek-yaw''\\\\ \\\\beh-rek-yaw''-hoo\\\\', 'from \\\\01290\\\\ and \\\\03050\\\\, Greek \\\\914\\\\ \\~baraciav\\~', '', 'n pr m', 'Berechiah 10, Berachiah 1', 11, ' Berachiah or Berechiah = "Jehovah blesses"\r\n\r\n 1) a son of Zerubbabel\r\n 2) a Levite who returned for the exile\r\n 3) father of Meshullam, one of Nehemiah''s chiefs who assisted in\r\n    rebuilding the walls of Jerusalem\r\n 4) father of Zechariah\r\n 5) a Gershonite Levite, the father of Asaph, the singer, doorkeeper\r\n    of the ark\r\n 6) an Ephraimite chief in the time of Ahaz\r\n');
