(1315, 'Bosmath', '\\^tmsb\\^ Bosmath', '\\\\bos-math''\\\\', 'from \\\\01314\\\\ (the second form) fragrance', '', 'n pr f', 'Bashemath 6, Basmath 1', 7, ' Bashemath or Basmath = "spice"\r\n\r\n 1) Hittite wife of Esau, daughter of Ishmael, sister of Nebaioth\r\n 2) daughter of Solomon, wife of Ahimaaz, one of Solomon''s commissariat\r\n    officers\r\n'),
(1316, 'Bashan', '\\^Nvb\\^ Bashan', '\\\\baw-shawn''\\\\', 'of uncertain derivation', '', 'n pr loc', 'Bashan 59, Bashanhavothjair + \\\\02334\\\\', 60, ' Bashan = "fruitful"\r\n\r\n 1) a district east of the Jordan known for its fertility which was\r\n    given to the half-tribe of Manasseh\r\n'),
(1317, 'boshnah', '\\^hnvb\\^ boshnah', '\\\\bosh-naw''\\\\', 'from \\\\0954\\\\', 'TWOT - 222b', 'n m', 'shame 1', 1, ' 1) shame\r\n    1a) shame\r\n    1b) shameful thing\r\n'),
(1318, 'bashac', '\\^ovb\\^ bashac', '\\\\baw-shas''\\\\', 'a primitive root', 'TWOT - 294', 'v', 'treading 1', 1, ' 1) to tread down, trample\r\n    1a) (Poel) trampling\r\n'),
(1319, 'basar', '\\^rsb\\^ basar', '\\\\baw-sar''\\\\', 'a primitive root', 'TWOT - 291', 'v', 'tidings 16, show forth 3, publish 3, messenger 1, preached 1', 24, ' 1) to bear news, bear tidings, publish, preach, show forth\r\n    1a) (Piel)\r\n        1a1) to gladden with good news\r\n        1a2) to bear news\r\n        1a3) to announce (salvation) as good news, preach\r\n    1b) (Hithpael) to receive good news\r\n'),
(1320, 'basar', '\\^rsb\\^ basar', '\\\\baw-sawr''\\\\', 'from \\\\01319\\\\', 'TWOT - 291a', 'n m', 'flesh 256, body 2, fatfleshed + \\\\01277\\\\ 2, leanfleshed + \\\\01851\\\\ 2, kin 2, leanfleshed + \\\\07534\\\\ 1, mankind + \\\\0376\\\\ 1, myself 1, nakedness 1, skin 1', 269, ' 1) flesh\r\n    1a) of the body\r\n        1a1) of humans\r\n        1a2) of animals\r\n    1b) the body itself\r\n    1c) male organ of generation (euphemism)\r\n    1d) kindred, blood-relations\r\n    1e) flesh as frail or erring (man against God)\r\n    1f) all living things\r\n    1g) animals\r\n    1h) mankind\r\n'),
