INSERT INTO `hebrew` (`id`, `word_id`, `word`, `pron`, `origin`, `source`, `kind`, `av`, `count`, `definition`) VALUES
(2219, 'zarah', '\\^hrz\\^ zarah', '\\\\zaw-raw''\\\\', 'a primitive root [compare \\\\02114\\\\]', 'TWOT - 579', 'v', 'scatter 19, disperse 8, fan 4, spread 2, winnowed 2, cast away 1, scatter away 1, compass 1, strawed 1', 39, ' 1) to scatter, fan, cast away, winnow, disperse, compass, spread, be\r\n    scattered, be dispersed\r\n    1a) (Qal)\r\n        1a1) to scatter\r\n        1a2) to fan, winnow\r\n    1b) (Niphal) to be scattered, be dispersed\r\n    1c) (Piel)\r\n        1c1) to scatter, disperse (intensive of Qal)\r\n        1c2) to winnow, sift\r\n    1d) (Pual) to be scattered, be spread out\r\n'),
(2220, 'z@rowa`', '\\^ewrz\\^ z@rowa` or (shortened)  \\^erz\\^ z@roa` and (fem.)  \\^hewrz\\^ z@row`ah or  \\^herz\\^ z@ro`ah', '\\\\zer-o''-ah\\\\ \\\\zer-o''-ah\\\\ \\\\zer-o-aw''\\\\ \\\\zer-o-aw''\\\\', 'from \\\\02232\\\\', 'TWOT - 583a', 'n f', 'arm 83, power 3, shoulder 2, holpen 1, mighty 1, strength 1', 91, ' 1) arm, forearm, shoulder, strength\r\n    1a) arm\r\n    1b) arm (as symbol of strength)\r\n    1c) forces (political and military)\r\n    1d) shoulder (of animal sacrificed)\r\n'),
(2221, 'zeruwa`', '\\^ewrz\\^ zeruwa`', '\\\\zay-roo''-ah\\\\', 'from \\\\02232\\\\', 'TWOT - 582b', 'n m', 'sowing 1, things that are sown 1', 2, ' 1) that which is sown, sowing, thing sown\r\n'),
(2222, 'zarziyph', '\\^Pyzrz\\^ zarziyph', '\\\\zar-zeef''\\\\', 'by reduplication from an unused root meaning to flow', 'TWOT - 584a', 'v, n m', 'water 1', 1, ' v\r\n 1) drip\r\n    1a) (Hiph) to cause to drip\r\n\r\n n m\r\n 2) drop, dripping, a soaking, a saturation\r\n'),
(2223, 'zarziyr', '\\^ryzrz\\^ zarziyr', '\\\\zar-zeer''\\\\', 'by reduplication from \\\\02115\\\\', 'TWOT - 543b', 'n m', 'greyhound 1', 1, ' 1) girded, girt, alert, used with \\\\04975\\\\\r\n    in \\\\#Pr 30:31\\\\\r\n    1a) perhaps an extinct animal, exact meaning unknown\r\n'),
(2224, 'zarach', '\\^xrz\\^ zarach', '\\\\zaw-rakh''\\\\', 'a primitive root', 'TWOT - 580', 'v', 'arise 8, rise 6, rise up 2, shine 1, up 1', 18, ' 1) to rise, come forth, break out, arise, rise up, shine\r\n    1a) (Qal)\r\n        1a1) to rise\r\n        1a2) to come out, appear\r\n'),
(2225, 'zerach', '\\^xrz\\^ zerach', '\\\\zeh''-rakh\\\\', 'from \\\\02224\\\\', 'TWOT - 580a', 'n m', 'rising 1', 1, ' 1) dawning, shining\r\n'),
(2226, 'Zerach', '\\^xrz\\^ Zerach', '\\\\zeh''-rakh\\\\', 'the same as \\\\02225\\\\, Greek \\\\2196\\\\ \\~Zara\\~', '', 'n pr m', 'Zerah 20, Zarah 1', 21, ' Zerah or Zarah = "rising"\r\n\r\n 1) a son of Reuel and grandson of Esau, one of the dukes of the Edomites\r\n 2) twin brother of Pharez, sons of Judah and Tamar; descendants are\r\n    called Zarhites, Ezrahites, and Izrahites\r\n 3) son of Simeon; also called ''Zohar''\r\n 4) a Gershonite Levite, son of Iddo of Adaiah\r\n 5) the Ethiopian or Cushite, invader of Judah in the reign of Asa who\r\n    defeated the invaders; probably the same as the Egyptian king\r\n    Usarken I, second king of the Egyptian 22nd dynasty or perhaps more\r\n    probably Usarken II, his 2nd successor\r\n 6) another Edomite leader; possibly same as 1\r\n'),
(2227, 'Zarchiy', '\\^yxrz\\^ Zarchiy', '\\\\zar-khee''\\\\', 'patronymically from \\\\02226\\\\', '', 'n patr m', 'Zarhites 6', 6, ' Zarhites = see Zerah\r\n\r\n 1) descendants of Zerah\r\n'),
(2228, 'Z@rachyah', '\\^hyxrz\\^ Z@rachyah', '\\\\zer-akh-yaw''\\\\', 'from \\\\02225\\\\ and \\\\03050\\\\', '', 'n pr m', 'Zerahiah 5', 5, ' Zerahiah = "Jehovah has risen"\r\n\r\n 1) a priest, son of Uzzi, and ancestor of Ezra the scribe\r\n 2) father of Elihoenai of the sons of Pahath-moab, whose descendants\r\n    returned from the captivity with Ezra\r\n'),
(2229, 'zaram', '\\^Mrz\\^ zaram', '\\\\zaw-ram''\\\\', 'a primitive root', 'TWOT - 581', 'v', 'pour out 1, carry away as with a flood 1', 2, ' 1) to pour out, pour forth in floods, flood away\r\n    1a) (Qal) to pour out, flood away\r\n    1b) (Poal) to pour forth, pour out\r\n        1b1) of God''s power (fig.)\r\n'),
(2230, 'zerem', '\\^Mrz\\^ zerem', '\\\\zeh''-rem\\\\', 'from \\\\02229\\\\', 'TWOT - 581a', 'n m', 'flood 1, overflowing 1, shower 1, storm 3, tempest 3', 9, ' 1) rain-shower, thunderstorm, flood of rain, downpour, rain-storm\r\n'),
(2231, 'zirmah', '\\^hmrz\\^ zirmah', '\\\\zir-maw''\\\\', 'from \\\\02230\\\\', 'TWOT - 581b', 'n f', 'issue 2', 2, ' 1) flow, issue\r\n    1a) issue (of seminal discharge)\r\n'),
(2232, 'zara`', '\\^erz\\^ zara`', '\\\\zaw-rah''\\\\', 'a primitive root', 'TWOT - 582', 'v', 'sow 47, yielding 3, sower 2, bearing 1, conceive 1, seed 1, set 1', 56, ' 1) to sow, scatter seed\r\n    1a) (Qal)\r\n        1a1) to sow\r\n        1a2) producing, yielding seed\r\n    1b) (Niphal)\r\n        1b1) to be sown\r\n        1b2) to become pregnant, be made pregnant\r\n    1c) (Pual) to be sown\r\n    1d) (Hiphil) to produce seed, yield seed\r\n'),
(2233, 'zera`', '\\^erz\\^ zera`', '\\\\zeh''-rah\\\\', 'from \\\\02232\\\\', 'TWOT - 582a', 'n m', 'seed 221, child 2, carnally + \\\\07902\\\\ 2, carnally 1, fruitful 1, seedtime 1, sowing time 1', 229, ' 1) seed, sowing, offspring\r\n    1a) a sowing\r\n    1b) seed\r\n    1c) semen virile\r\n    1d) offspring, descendants, posterity, children\r\n    1e) of moral quality\r\n        1e1) a practitioner of righteousness (fig.)\r\n    1f) sowing time (by meton)\r\n'),
