(3440, 'Yishviy', '\\^ywvy\\^ Yishviy', '\\\\yish-vee''\\\\', 'from \\\\07737\\\\', '', 'n pr m', 'Ishui 1, Ishuai 1, Isui 1, Jesui 1', 4, ' Ishui or Ishuai or Isui or Jesui = "he resembles me"\r\n\r\n 1) the 2nd son of Saul by his wife Ahinoam\r\n 2) the 3rd son of Asher and founder of the family of Ishuaites\r\n'),
(3441, 'Yishviy', '\\^ywvy\\^ Yishviy', '\\\\yish-vee''\\\\', 'patronymically from \\\\03440\\\\', '', 'adj', 'Jesuites 1', 1, ' Jesuites = see Jesui "he will justify me"\r\n\r\n 1) the descendants of Jesui (\\\\03440\\\\-2)\r\n'),
(3442, 'Yeshuwa`', '\\^ewvy\\^ Yeshuwa`', '\\\\yay-shoo''-ah\\\\', 'for \\\\03091\\\\', '', '', 'Jeshua 29', 29, ' Jeshua = "he is saved"\r\n\r\n n pr m\r\n 1) son of Nun of the tribe of Ephraim and successor to Moses as the\r\n    leader of the children of Israel; led the conquest of Canaan\r\n 2) son of Jehozadak and high priest after the restoration\r\n 3) a priest in the time of David who had charge of the 9th course\r\n 4) a Levite in the reign of Hezekiah\r\n 5) head of a Levitical house which returned from captivity in Babylon\r\n 6) father of a builder of the wall of Jerusalem in the time of Nehemiah\r\n\r\n n pr loc\r\n 7) a town in southern Judah reinhabited by the people of Judah after\r\n    the return from captivity\r\n'),
(3443, 'Yeshuwa`', '\\^ewvy\\^ Yeshuwa` (Aramaic)', '\\\\yay-shoo''-ah\\\\', 'corresponding to \\\\03442\\\\', '', 'n pr m', 'Jeshua 1', 1, ' Jeshua = "he is saved"\r\n\r\n 1) an exilic priest who returned with Zerubbabel\r\n'),
(3444, 'y@shuw`ah', '\\^hewvy\\^ y@shuw`ah', '\\\\yesh-oo''-aw\\\\', 'passive participle of \\\\03467\\\\', 'TWOT - 929b', 'n f', 'salvation 65, help 4, deliverance 3, health 3, save 1, saving 1, welfare 1', 78, ' 1) salvation, deliverance\r\n    1a) welfare, prosperity\r\n    1b) deliverance\r\n    1c) salvation (by God)\r\n    1d) victory\r\n'),
(3445, 'yeshach', '\\^xvy\\^ yeshach', '\\\\yeh''-shakh\\\\', 'from an unused root meaning to gape (as the empty stomach)', 'TWOT - 924', 'n m', 'casting down 1', 1, ' 1) a sinking feeling, emptiness\r\n'),
(3446, 'Yischaq', '\\^qxuy\\^ Yischaq', '\\\\yis-khawk''\\\\', 'from \\\\07831\\\\', 'TWOT - 1905b', 'n pr m', 'Isaac 4', 4, ' Isaac = "he laughs"\r\n\r\n 1) son of Abraham by Sarah his wife and father of Jacob and Esau\r\n'),
(3447, 'yashat', '\\^jvy\\^ yashat', '\\\\yaw-shat''\\\\', 'a primitive root', 'TWOT - 925', 'v', 'hold out 3', 3, ' 1) (Hiphil) to hold out, extend\r\n'),
(3448, 'Yishay', '\\^yvy\\^ Yishay by Aramaic  \\^yvya\\^ ''Iyshay', '\\\\yee-shah''-ee\\\\ \\\\ee-shah''-ee\\\\', 'from the same as \\\\03426\\\\, Greek \\\\2421\\\\ \\~Iessai\\~', 'TWOT - 926', 'n pr m', 'Jesse 42', 42, ' Jesse = "I possess"\r\n\r\n 1) son of Boaz and the father of king David\r\n'),
(3449, 'Yishshiyah', '\\^hyvy\\^ Yishshiyah or  \\^whyvy\\^ Yishshiyahuw', '\\\\yish-shee-yaw''\\\\ \\\\yish-shee-yaw''-hoo\\\\', 'from \\\\05383\\\\ and \\\\03050\\\\', '', 'n pr m', 'Isshiah 3, Jesiah 2, Ishiah 1, Ishijah 1', 7, ' Ishiah or Ishijah or Jesiah = "Jehovah will lend"\r\n\r\n 1) a Korhite, one of David''s mighty warriors who joined him at Ziklag\r\n 2) the 5th of 5 sons of Izrahiah and one of the heads of the tribe\r\n    of Issachar in the time of David\r\n 3) a Levite, the 2nd son of Uzziel and grandson of Kohath\r\n 4) another Levite, son of Rehabiah\r\n 5) a descendant of Harim who had a foreign wife\r\n'),
(3450, 'Y@siyma''el', '\\^lamysy\\^ Y@siyma''el', '\\\\yes-eem-aw-ale''\\\\', 'from \\\\07760\\\\ and \\\\0410\\\\', '', 'n pr m', 'Jesimiel 1', 1, ' Jesimiel = "God will place"\r\n\r\n 1) a Simeonite chief of the family of Shimei\r\n'),
