(3454, 'Y@shiyshay', '\\^yvyvy\\^ Y@shiyshay', '\\\\yesh-ee-shah''-ee\\\\', 'from \\\\03453\\\\', '', 'n pr m', 'Jeshishai 1', 1, ' Jeshishai = "my old one"\r\n\r\n 1) a Gadite from Gilead\r\n'),
(3455, 'yasam', '\\^Msy\\^ yasam', '\\\\yaw-sam''\\\\', 'a primitive root', 'TWOT - 2243', 'v', 'put 1, variant 1', 2, ' 1) to put, place, set, appoint, make\r\n    1a) (Qal) to put, place\r\n'),
(3456, 'yasham', '\\^Mvy\\^ yasham', '\\\\yaw-sham''\\\\', 'a primitive root', 'TWOT - 927', 'v', 'desolate 4', 4, ' 1) (Qal) to ruin, be desolate\r\n'),
