(3555, 'k@viyah', '\\^hywk\\^ k@viyah', '\\\\kev-ee-yaw''\\\\', 'from \\\\03554\\\\', 'TWOT - 961b', 'n f', 'burning 2', 2, ' 1) burning, branding, branding scar, burn\r\n'),
(3556, 'kowkab', '\\^bkwk\\^ kowkab', '\\\\ko-kawb''\\\\', 'probably from the same as \\\\03522\\\\ (in the sense of rolling) or \\\\03554\\\\ (in the sense of blazing)', 'TWOT - 942a', 'n m', 'star 36, stargazers + \\\\02374\\\\ 1', 37, ' 1) star\r\n    1a) of Messiah, brothers, youth, numerous progeny, personification,\r\n        God''s omniscience (fig.)\r\n'),
(3557, 'kuwl', '\\^lwk\\^ kuwl', '\\\\kool\\\\', 'a primitive root', 'TWOT - 962', 'v', 'contain 6, feed 6, sustain 4, abide 3, nourish 3, hold 2, receive 2, victual 2, bear 1, comprehended 1, misc 7', 37, ' 1) to seize, contain, measure\r\n    1a) (Qal) to measure, calculate\r\n    1b) (Pilpel) to sustain, maintain, contain\r\n        1b1) to sustain, support, nourish\r\n        1b2) to contain, hold in, restrain\r\n        1b3) to support, endure\r\n    1c) (Polpal) to be supplied\r\n    1d) (Hilpil) to contain, hold, hold in, endure\r\n        1d1) to contain\r\n        1d2) to sustain, endure\r\n'),
(3558, 'kuwmaz', '\\^zmwk\\^ kuwmaz', '\\\\koo-mawz''\\\\', 'from an unused root meaning to store away', 'TWOT - 990a', 'n m', 'tablets 2', 2, ' 1) ornaments, golden ornament\r\n    1a) maybe - armlets of gold, exact meaning not known\r\n 2) (TWOT) tablets\r\n'),
(3559, 'kuwn', '\\^Nwk\\^ kuwn', '\\\\koon\\\\', 'a primitive root', 'TWOT - 964', 'v', 'prepare 85, establish 58, ready 17, stablish 5, provide 5, right 5, fixed 4, set 4, direct 3, order 3, fashion 3, variant 2, certain 2, confirmed 2, firm 2, preparation 2, misc 17', 219, ' 1) to be firm, be stable, be established\r\n    1a) (Niphal)\r\n        1a1) to be set up, be established, be fixed\r\n             1a1a) to be firmly established\r\n             1a1b) to be established, be stable, be secure, be enduring\r\n             1a1c) to be fixed, be securely determined\r\n        1a2) to be directed aright, be fixed aright, be steadfast\r\n             (moral sense)\r\n        1a3) to prepare, be ready\r\n        1a4) to be prepared, be arranged, be settled\r\n    1b) (Hiphil)\r\n        1b1) to establish, set up, accomplish, do, make firm\r\n        1b2) to fix, make ready, prepare, provide, provide for, furnish\r\n        1b3) to direct toward (moral sense)\r\n        1b4) to arrange, order\r\n    1c) (Hophal)\r\n        1c1) to be established, be fastened\r\n        1c2) to be prepared, be ready\r\n    1d) (Polel)\r\n        1d1) to set up, establish\r\n        1d2) to constitute, make\r\n        1d3) to fix\r\n        1d4) to direct\r\n    1e) (Pulal) to be established, be prepared\r\n    1f) (Hithpolel) to be established, be restored\r\n'),
(3560, 'Kuwn', '\\^Nwk\\^ Kuwn', '\\\\koon\\\\', 'probably from \\\\03559\\\\', '', 'n pr loc', 'Chun 1', 1, ' Chun = "established"\r\n\r\n 1) a city belonging to Hadarezer, plundered by David''s forces\r\n'),
