(3575, 'Kuwth', '\\^twk\\^ Kuwth or (fem.)  \\^htwk\\^ Kuwthah', '\\\\kooth\\\\ \\\\koo-thaw''\\\\', 'of foreign origin', '', 'n pr loc', 'Cuth 1, Cuthah 1', 2, ' Cuth = "crushing"\r\n\r\n 1) a place from which king Sargon of Assyria imported colonists into\r\n    Israel; probably a location approx 20 (32 km) miles northeast of\r\n    Babylon\r\n'),
(3576, 'kazab', '\\^bzk\\^ kazab', '\\\\kaw-zab''\\\\', 'a primitive root', 'TWOT - 970', 'v', 'lie 11, liar 3, vain 1, fail 1', 16, ' 1) to lie, tell a lie, be a liar, be found a liar, be in vain, fail\r\n    1a) (Qal) liar (participle)\r\n    1b) (Niphal) to be proven to be lying\r\n    1c) (Piel)\r\n        1c1) to lie, tell a lie, tell a lie with, deceive\r\n        1c2) to disappoint, fail\r\n    1d) (Hiphil) to make a liar, prove to be a liar\r\n'),
(3577, 'kazab', '\\^bzk\\^ kazab', '\\\\kaw-zawb''\\\\', 'from \\\\03576\\\\', 'TWOT - 970a', 'n m', 'lie 23, lying 2, leasing 2, deceitful 1, false 1, liar 1, lies + \\\\01697\\\\ 1', 31, ' 1) a lie, untruth, falsehood, deceptive thing\r\n'),
(3578, 'Koz@ba''', '\\^abzk\\^ Koz@ba''', '\\\\ko-zeb-aw''\\\\', 'from \\\\03576\\\\', '', 'n pr loc', 'Chozeba 1', 1, ' Chozeba = "falsehood"\r\n\r\n 1) a city in Judah\r\n'),
(3579, 'Kozbiy', '\\^ybzk\\^ Kozbiy', '\\\\koz-bee''\\\\', 'from \\\\03576\\\\', '', 'n pr f', 'Cozbi 2', 2, ' Cozbi = "my lie"\r\n\r\n 1) a daughter of Zur, a chief of the Midianites, who was slain by\r\n    Phinehas because of her adultery\r\n'),
(3580, 'K@ziyb', '\\^byzk\\^ K@ziyb', '\\\\kez-eeb''\\\\', 'from \\\\03576\\\\', '', 'n pr loc', 'Chezib 1', 1, ' Chezib = "false"\r\n\r\n 1) a town in Judah\r\n'),
(3581, 'koach', '\\^xk\\^ koach or (\\\\#Da 11:6\\\\)  \\^xwk\\^ kowach', '\\\\ko''-akh\\\\ \\\\ko''-akh\\\\', 'from an unused root meaning to be firm', 'TWOT - 973.1', 'n m', 'strength 58, power 47, might 7, force 3, ability 2, able 2, able + \\\\06113\\\\ 1, chameleon 1, fruits 1, powerful 1, substance 1, wealth 1, weary + \\\\03019\\\\ 1', 126, ' 1) strength, power, might\r\n    1a) human strength\r\n    1b) strength (of angels)\r\n    1c) power (of God)\r\n    1d) strength (of animals)\r\n    1e) strength, produce, wealth (of soil)\r\n 2) a small reptile, probably a kind of lizard, which is unclean\r\n    2a) perhaps an extinct animal, exact meaning is unknown\r\n'),
(3582, 'kachad', '\\^dxk\\^ kachad', '\\\\kaw-khad''\\\\', 'a primitive root', 'TWOT - 972', 'v', 'hide 16, cut off 10, conceal 4, desolate 1, cut down 1', 32, ' 1) to hide, conceal, cut off, cut down, make desolate, kick\r\n    1a) (Niphal)\r\n        1a1) to be hidden\r\n        1a2) to be effaced, be destroyed, be cut off\r\n    1b) (Piel) to cover, hide\r\n    1c) (Hiphil)\r\n        1c1) to hide\r\n        1c2) to efface, annihilate\r\n'),
(3583, 'kachal', '\\^lxk\\^ kachal', '\\\\kaw-khal''\\\\', 'a primitive root', 'TWOT - 974', 'v', 'paint 1', 1, ' 1) (Qal) to paint (eyes), adorn with paint\r\n'),
