(3749, 'karkob', '\\^bkrk\\^ karkob', '\\\\kar-kobe''\\\\', 'expanded from the same as \\\\03522\\\\', 'TWOT - 1038a', 'n m', 'compass 2', 2, ' 1) edge, rim\r\n'),
(3750, 'karkom', '\\^Mkrk\\^ karkom', '\\\\kar-kome''\\\\', 'probably of foreign origin', 'TWOT - 1039', 'n m', 'saffron 1', 1, ' 1) saffron\r\n    1a) an orange-yellow flower of the crocus family, used for flavouring\r\n       in drinks and confections in order to promote perspiration for\r\n       cooling of the body\r\n'),
(3751, 'Kark@miysh', '\\^vymkrk\\^ Kark@miysh', '\\\\kar-kem-eesh''\\\\', 'of foreign derivation', '', 'n pr loc', 'Carchemish 3', 3, ' Carchemish = "fortress of Chemosh"\r\n\r\n 1) a Hittite capital northeast of Israel on the Euphrates river\r\n    captured by Pharaoh Necho and then from him by Nebuchadnezzar\r\n'),
(3752, 'Karkac', '\\^okrk\\^ Karkac', '\\\\kar-kas''\\\\', 'of Persian origin', '', 'n pr m', 'Carcas 1', 1, ' Carcas = "severe"\r\n\r\n 1) the 7th of the 7 eunuchs of king Ahasuerus\r\n'),
