(3754, 'kerem', '\\^Mrk\\^ kerem', '\\\\keh''-rem\\\\', 'from an unused root of uncertain meaning', 'TWOT - 1040a', 'n m', 'vineyard 89, vines 3, vintage 1', 93, ' 1) vineyard\r\n'),
(3755, 'korem', '\\^Mrk\\^ korem', '\\\\ko-rame''\\\\', 'act. participle of an imaginary denominative from \\\\03754\\\\', 'TWOT - 1040', 'v', 'vinedresser 5', 5, ' 1) to tend vines or vineyards, to dress vines or vineyards\r\n    1a) (Qal) vinedressers (participle)\r\n'),
(3756, 'Karmiy', '\\^ymrk\\^ Karmiy', '\\\\kar-mee''\\\\', 'from \\\\03754\\\\', '', 'n pr m', 'Carmi 8', 8, ' Carmi = "my vineyard"\r\n\r\n 1) the 4th son of Reuben and progenitor of the Carmites\r\n 2) a Judaite, father of Achan, the ''troubler of Israel'' in the time\r\n    of Joshua\r\n'),
(3757, 'Karmiy', '\\^ymrk\\^ Karmiy', '\\\\kar-mee''\\\\', 'patronymically from \\\\03756\\\\', '', 'adj', 'Carmites 1', 1, ' Carmites = see Carmi "my vineyard"\r\n\r\n 1) the descendants of Carmi, the 4th son of Reuben\r\n'),
(3758, 'karmiyl', '\\^lymrk\\^ karmiyl', '\\\\kar-mele''\\\\', 'probably of foreign origin', 'TWOT - 1043', 'n m', 'crimson 3', 3, ' 1) crimson, red, carmine\r\n'),
(3759, 'karmel', '\\^lmrk\\^ karmel', '\\\\kar-mel''\\\\', 'from \\\\03754\\\\', 'TWOT - 1041', 'n m', 'fruitful field 7, plentiful field 2, full ear 1, green ear 1, full ears of corn 1, plentiful 1', 13, ' 1) plantation, garden-land, orchard, fruit orchard\r\n 2) fruit, garden-growth (meton)\r\n'),
(3760, 'Karmel', '\\^lmrk\\^ Karmel', '\\\\kar-mel''\\\\', 'the same as \\\\03759\\\\', 'TWOT - 1042', 'n pr loc', 'Carmel 26', 26, ' Carmel = "garden-land"\r\n\r\n 1) a mountain on the Mediterranean coast of northern Israel, just\r\n    below Haifa\r\n 2) a town in the mountains on the west side of the Dead Sea and south\r\n    of Hebron\r\n'),
(3761, 'Karm@liy', '\\^ylmrk\\^ Karm@liy', '\\\\kar-mel-ee''\\\\', 'patron from \\\\03760\\\\', '', 'adj', 'Carmelite 5', 5, ' Carmelite = see Carmel "garden-land"\r\n\r\n 1) an inhabitant of Carmel\r\n'),
(3762, 'Karm@liyth', '\\^tylmrk\\^ Karm@liyth', '\\\\kar-mel-eeth''\\\\', 'feminine of \\\\03761\\\\', '', 'adj', 'Carmelitess 2', 2, ' Carmelitess = see Carmel "garden-land"\r\n\r\n 1) a female inhabitant of Carmel\r\n 2) Abigail, the wife of David, who came from Carmel\r\n'),
(3763, 'K@ran', '\\^Nrk\\^ K@ran', '\\\\ker-awn''\\\\', 'of uncertain derivation', '', 'n pr m', 'Cheran 2', 2, ' Cheran = "lyre"\r\n\r\n 1) an Edomite, one of the sons of Dishon, the Horite duke\r\n'),
(3764, 'korce''', '\\^aork\\^ korce'' (Aramaic)', '\\\\kor-say''\\\\', 'corresponding to \\\\03678\\\\', 'TWOT - 2803', 'n m', 'throne 3', 3, ' 1) throne, royal chair\r\n'),
(3765, 'kircem', '\\^Mork\\^ kircem', '\\\\kir-same''\\\\', 'from \\\\03697\\\\', 'TWOT - 1013b', 'v', 'waste 1', 1, ' 1) (Piel) to tear apart, ravage, tear off\r\n'),
(3766, 'kara`', '\\^erk\\^ kara`', '\\\\kaw-rah''\\\\', 'a primitive root', 'TWOT - 1044', 'v', 'bow 14, ...down 12, fell 2, subdued 2, brought low 1, couched 1, feeble 1, kneeling 1, very 1', 36, ' 1) to bend, kneel, bow, bow down, sink down to one''s knees, kneel\r\n    down to rest (of animals), kneel in reverence\r\n    1a) (Qal)\r\n        1a1) to bow\r\n        1a2) to bow down, crouch\r\n        1a3) to bow down over\r\n        1a4) to tilt, lean\r\n    1b) (Hiphil) to cause to bow\r\n'),
(3767, 'kara`', '\\^erk\\^ kara` or (dual)  \\^Myerk\\^', '\\\\kaw-raw''\\\\', 'from \\\\03766\\\\', 'TWOT - 1044a', 'n f', 'leg 9', 9, ' 1) leg\r\n'),
(3768, 'karpac', '\\^oprk\\^ karpac', '\\\\kar-pas''\\\\', 'of foreign origin', 'TWOT - 1045', 'n m', 'green 1', 1, ' 1) cotton or fine linen\r\n'),
