(3770, 'k@res', '\\^srk\\^ k@res', '\\\\ker-ace''\\\\', 'by variation from \\\\07164\\\\', 'TWOT - 1047a', 'n m', 'belly 1', 1, ' 1) belly\r\n'),
(3771, 'Karsh@na''', '\\^anvrk\\^ Karsh@na''', '\\\\kar-shen-aw''\\\\', 'of foreign origin', '', 'n pr m', 'Carshena 1', 1, ' Carshena = "illustrious"\r\n\r\n 1) one of the 7 princes of Persia and Media under king Ahasuerus\r\n'),
(3772, 'karath', '\\^trk\\^ karath', '\\\\kaw-rath''\\\\', 'a primitive root', 'TWOT - 1048', 'v', 'cut off 145, make 85, cut down 23, cut 9, fail 6, destroy 4, want 3, covenanted 2, hew 2, misc 9', 288, ' 1) to cut, cut off, cut down, cut off a body part, cut out, eliminate,\r\n    kill, cut a covenant\r\n    1a) (Qal)\r\n        1a1) to cut off\r\n             1a1a) to cut off a body part, behead\r\n        1a2) to cut down\r\n        1a3) to hew\r\n        1a4) to cut or make a covenant\r\n    1b) (Niphal)\r\n        1b1) to be cut off\r\n        1b2) to be cut down\r\n        1b3) to be chewed\r\n        1b4) to be cut off, fail\r\n    1c) (Pual)\r\n        1c1) to be cut off\r\n        1c2) to be cut down\r\n    1d) (Hiphil)\r\n        1d1) to cut off\r\n        1d2) to cut off, destroy\r\n        1d3) to cut down, destroy\r\n        1d4) to take away\r\n        1d5) to permit to perish\r\n    1e) (Hophal) cut off\r\n'),
(3773, 'karuthah', '\\^htrk\\^ karuthah or (plural)  \\^twtrk\\^', '\\\\kaw-rooth-aw''\\\\', 'passive participle of \\\\03772\\\\', 'TWOT - 1048b', 'n f', 'beam 3', 3, ' 1) beams, hewn beams\r\n'),
(3774, 'K@rethiy', '\\^ytrk\\^ K@rethiy', '\\\\ker-ay-thee''\\\\', 'probably from \\\\03772\\\\ in the sense of executioner', '', 'adj', 'Cherethites 10', 10, ' Cherethites = "executioners"\r\n\r\n 1) a group of foreign mercenary soldiers serving as a bodyguard for\r\n    king David; also executioners\r\n 2) either Cretans or proto-Philistines (in general)\r\n'),
(3775, 'keseb', '\\^bsk\\^ keseb', '\\\\keh''-seb\\\\', 'apparently by transposition for \\\\03532\\\\', 'TWOT - 949', 'n m', 'sheep 9, lamb 4', 13, ' 1) lamb, young ram, sheep\r\n'),
