(3777, 'Kesed', '\\^dsk\\^ Kesed', '\\\\keh''-sed\\\\', 'from an unused root of uncertain meaning', '', 'n pr m', 'Chesed 1', 1, ' Chesed = "increase"\r\n\r\n 1) the 4th son of Nahor, the nephew of Abraham\r\n'),
(3778, 'Kasdiy', '\\^ydsk\\^ Kasdiy (occasionally with enclitic)  \\^hmydsk\\^ Kasdiymah', '\\\\kas-dee''\\\\ \\\\kas-dee''-maw\\\\', 'from \\\\03777\\\\ (only in the plur.), Greek \\\\5466\\\\ \\~Xaldaiov\\~', '', 'n pr', 'Chaldeans 59, Chaldees 14, Chaldea 7', 80, ' Chaldea or Chaldeans = "clod-breakers"\r\n\r\n n pr loc\r\n 1) a territory in lower Mesopotamia bordering on the Persian Gulf\r\n\r\n n pr m\r\n 2) the inhabitants of Chaldea, living on the lower Euphrates and Tigris\r\n 3) those persons considered the wisest in the land (by extension)\r\n'),
(3779, 'Kasday', '\\^ydsk\\^ Kasday (Aramaic)', '\\\\kas-dah''-ee\\\\', 'corresponding to \\\\03778\\\\', '', 'n m pater', 'Chaldean 8', 8, ' Chaldean = "clod breakers"\r\n\r\n 1) the inhabitants of Chaldea, living on the lower Euphrates and Tigris\r\n 2) those persons considered the wisest in the land (by extension)\r\n'),
(3780, 'kasah', '\\^hsk\\^ kasah', '\\\\kaw-saw''\\\\', 'a primitive root', 'TWOT - 1049', 'v', 'covered 1', 1, ' 1) (Qal) to become sated, be gorged with food\r\n'),
(3781, 'kashshiyl', '\\^lyvk\\^ kashshiyl', '\\\\kash-sheel''\\\\', 'from \\\\03782\\\\', 'TWOT - 1050a', 'n m', 'axes 1', 1, ' 1) axe\r\n'),
(3782, 'kashal', '\\^lvk\\^ kashal', '\\\\kaw-shal''\\\\', 'a primitive root', 'TWOT - 1050', 'v', 'fall 27, stumble 19, cast down 4, feeble 4, overthrown 2, ruin 2, bereave + \\\\07921\\\\ 1, decayed 1, faileth 1, utterly 1, weak 1, variant 2', 65, ' 1) to stumble, stagger, totter\r\n    1a) (Qal)\r\n        1a1) to stumble\r\n        1a2) to totter\r\n    1b) (Niphal)\r\n        1b1) to stumble\r\n        1b2) to be tottering, be feeble\r\n    1c) (Hiphil)\r\n        1c1) to cause to stumble, bring injury or ruin to, overthrow\r\n        1c2) to make feeble, make weak\r\n    1d) (Hophal) to be made to stumble\r\n    1e) (Piel) bereave\r\n'),
(3783, 'kishshalown', '\\^Nwlvk\\^ kishshalown', '\\\\kish-shaw-lone''\\\\', 'from \\\\03782\\\\', 'TWOT - 1050b', 'n m', 'fall 1', 1, ' 1) a stumbling, a fall, a calamity\r\n'),
(3784, 'kashaph', '\\^Pvk\\^ kashaph', '\\\\kaw-shaf''\\\\', 'a primitive root', 'TWOT - 1051', 'v', 'sorcerers 3, witch 2, witchcraft 1', 6, ' 1) (Piel) to practice witchcraft or sorcery, use witchcraft\r\n    1a) sorcerer, sorceress (participle)\r\n'),
(3785, 'kesheph', '\\^Pvk\\^ kesheph', '\\\\keh''-shef\\\\', 'from \\\\03784\\\\', 'TWOT - 1051a', 'n m', 'witchcraft 4, sorcery 2', 6, ' 1) sorcery, witchcraft\r\n'),
