(3787, 'kasher', '\\^rvk\\^ kasher', '\\\\kaw-share''\\\\', 'a primitive root', 'TWOT - 1052', 'v', 'right 1, prosper 1, direct 1', 3, ' 1) to succeed, please, be suitable, be proper, be advantageous, be\r\n    right and proper to\r\n    1a) (Qal) to please, be proper\r\n    1b) (Hiphil) to give success\r\n'),
(3788, 'kishrown', '\\^Nwrvk\\^ kishrown', '\\\\kish-rone''\\\\', 'from \\\\03787\\\\', 'TWOT - 1052b', 'n m', 'good 1, right 1, equity 1', 3, ' 1) success, skill, profit\r\n    1a) skill\r\n    1b) success, profit\r\n'),
(3789, 'kathab', '\\^btk\\^ kathab', '\\\\kaw-thab''\\\\', 'a primitive root', 'TWOT - 1053', 'v', 'write 210, describe 7, subscribe 4, recorded 1, write up 1', 223, ' 1) to write, record, enrol\r\n    1a) (Qal)\r\n        1a1) to write, inscribe, engrave, write in, write on\r\n        1a2) to write down, describe in writing\r\n        1a3) to register, enrol, record\r\n        1a4) to decree\r\n    1b) (Niphal)\r\n        1b1) to be written\r\n        1b2) to be written down, be recorded, be enrolled\r\n    1c) (Piel) to continue writing\r\n'),
(3790, 'k@thab', '\\^btk\\^ k@thab (Aramaic)', '\\\\keth-ab''\\\\', 'corresponding to \\\\03789\\\\', 'TWOT - 2805', 'v', 'write 8', 8, ' 1) to write\r\n    1a) (P''al) to write, to be written\r\n'),
(3791, 'kathab', '\\^btk\\^ kathab', '\\\\kaw-thawb''\\\\', 'from \\\\03789\\\\', 'TWOT - 1053a', 'n m', 'writing 14, register 2, scripture 1', 17, ' 1) a writing, document, edict\r\n    1a) register, enrolment, roll\r\n    1b) mode of writing, character, letter\r\n    1c) letter, document, a writing\r\n    1d) a written edict\r\n        1d1) of royal enactment\r\n        1d2) of divine authority\r\n'),
(3792, 'k@thab', '\\^btk\\^ k@thab (Aramaic)', '\\\\keth-awb''\\\\', 'corresponding to \\\\03791\\\\', 'TWOT - 2805a', 'n m', 'writing 10, written 1, prescribing 1', 12, ' 1) a writing\r\n    1a) writing, inscription\r\n    1b) written decree, written requirement\r\n'),
