(5709, '`ada''', '\\^ade\\^ `ada'' (Aramaic) or  \\^hde\\^ `adah (Aramaic)', '\\\\ad-aw''\\\\ \\\\ad-aw''\\\\', 'corresponding to \\\\05710\\\\', 'TWOT - 2898', 'v', 'take away 3, passed 1, departed 1, altereth 1, took 1, pass away 1, removeth 1', 9, ' 1) to pass on, pass away\r\n    1a) (P''al)\r\n        1a1) to pass on, pass over\r\n        1a2) to pass away\r\n    1b) (Aphel) to take away, remove, depose\r\n'),
(5710, '`adah', '\\^hde\\^ `adah', '\\\\aw-daw''\\\\', 'a primitive root', 'TWOT - 1565', 'v', 'deck ... 6, adorn 2, passed 1, take away 1', 10, ' 1) to pass on, advance, go on, pass by, remove\r\n    1a) (Qal) to pass on, put on\r\n    1b) (Hiphil) to remove\r\n'),
(5711, '`Adah', '\\^hde\\^ `Adah', '\\\\aw-daw''\\\\', 'from \\\\05710\\\\', '', 'n pr f', 'Adah 8', 8, ' Adah = "ornament"\r\n\r\n 1) the 1st of the 2 wives of Lamech and mother of Jabal and Jubal\r\n 2) a Hittitess, 1 of the 3 wives of Esau and mother of Eliphaz\r\n    2a) also called ''Bashemath''\r\n'),
(5712, '`edah', '\\^hde\\^ `edah', '\\\\ay-daw''\\\\', 'from \\\\05707\\\\ in the original sense of fixture', 'TWOT - 878a', 'n f', 'congregation 124, company 13, assembly 9, multitude 1, people 1, swarm 1', 149, ' 1) congregation, gathering\r\n'),
(5713, '`edah', '\\^hde\\^ `edah', '\\\\ay-daw''\\\\', 'from \\\\05707\\\\ in its technical sense', 'TWOT - 1576c,1576e', 'n f', 'testimonies 22, witness 4', 26, ' 1) testimony, witness\r\n    1a) always plural and always of laws as divine testimonies\r\n'),
(5714, '`Iddow', '\\^wde\\^ `Iddow or  \\^awde\\^ `Iddow'' or  \\^ayde\\^ `Iddiy''', '\\\\id-do''\\\\ \\\\id-do''\\\\ \\\\id-dee''\\\\', 'from \\\\05710\\\\', '', 'n pr m', 'Iddo 10', 10, ' Iddo = "His witness"\r\n\r\n 1) father of Abinadab, an officer of Solomon\r\n 2) grandfather of the prophet Zechariah\r\n 3) a Gershonite Levite, son of Joah\r\n 4) a priest in the time of Nehemiah\r\n 5) a seer in the time of king Jeroboam of the northern kingdom of Israel\r\n 6) son of Zechariah, ruler of the tribe of Manasseh in the time of David\r\n 7) a chief of the temple slaves who assembled at Casiphia at the\r\n    time of the 2nd caravan from Babylon\r\n'),
(5715, '`eduwth', '\\^twde\\^ `eduwth', '\\\\ay-dooth''\\\\', 'from \\\\05707\\\\', 'TWOT - 1576f', 'n f', 'testimony 55, witness 4', 59, ' 1) testimony\r\n'),
