(5729, '`Eden', '\\^Nde\\^ `Eden', '\\\\eh''-den\\\\', 'from \\\\05727\\\\', '', 'n pr loc', 'Eden 3', 3, ' Eden = "pleasure"\r\n\r\n 1) a place conquered by Assyria; probably located in the northwest of\r\n    Mesopotamia\r\n'),
(5730, '`eden', '\\^Nde\\^ `eden or (fem.)  \\^hnde\\^ `ednah', '\\\\ay''-den\\\\ \\\\ed-naw''\\\\', 'from \\\\05727\\\\', 'TWOT - 1567a', 'n m/f', 'pleasure 2, delights 1, delicates 1', 4, ' 1) luxury, dainty, delight, finery\r\n 2) delight\r\n'),
(5731, '`Eden', '\\^Nde\\^ `Eden', '\\\\ay''-den\\\\', 'the same as \\\\05730\\\\', 'TWOT - 1568', '', 'Eden 17', 17, ' Eden= "pleasure"\r\n\r\n n pr m loc\r\n 1) the first habitat of man after the creation; site unknown\r\n\r\n n pr m\r\n 2) a Gershonite Levite, son of Joah in the days of king Hezekiah of Judah\r\n'),
(5732, '`iddan', '\\^Nde\\^ `iddan (Aramaic)', '\\\\id-dawn''\\\\', 'from a root corresponding to that of \\\\05708\\\\', 'TWOT - 2900', 'n m', 'time 13', 13, ' 1) time\r\n    1a) time (of duration)\r\n    1b) year\r\n'),
(5733, '`Adna''', '\\^ande\\^ `Adna''', '\\\\ad-naw''\\\\', 'from \\\\05727\\\\', '', 'n pr m', 'Adna 2', 2, ' Adna = "rest" or "pleasure"\r\n\r\n 1) one of the family of Pahath-moab who returned with Ezra and\r\n    married a foreign wife\r\n 2) a priest, descendant of Harim in the days of Joiakim, the son of\r\n    Jeshua\r\n'),
(5734, '`Adnah', '\\^hnde\\^ `Adnah', '\\\\ad-naw''\\\\', 'from \\\\05727\\\\', '', 'n pr m', 'Adnah 2', 2, ' Adnah = "pleasure"\r\n\r\n 1) a Manassite who deserted from Saul and joined David at Ziklag\r\n 2) a captain in charge of over 300,000 men of Judah in the time of\r\n    Jehoshaphat\r\n'),
(5735, '`Ad`adah', '\\^hdede\\^ `Ad`adah', '\\\\ad-aw-daw''\\\\', 'from \\\\05712\\\\', '', 'n pr loc', 'Adadah 1', 1, ' Adadah = "festival" or "boundary"\r\n\r\n 1) one of the towns in the extreme south of Judah\r\n');
