(5736, '`adaph', '\\^Pde\\^ `adaph', '\\\\aw-daf''\\\\', 'a primitive root', 'TWOT - 1569', 'v', 'remains 4, overplus 1, more 1, odd number 1, over and above 1, over 1', 9, ' 1) to remain over, be in excess\r\n    1a) (Qal)\r\n        1a1) to be in excess\r\n             1a1a) excess (subst)\r\n             1a1b) surplus (participle)\r\n    1b) (Hiphil) to have a surplus\r\n'),
(5737, '`adar', '\\^rde\\^ `adar', '\\\\aw-dar''\\\\', 'a primitive root', 'TWOT - 1570,1571,1572', 'v', 'fail 4, lack 3, dig 2, keep 1, keep rank 1', 11, ' 1) to help\r\n    1a) (Qal) to help\r\n 2) to hoe\r\n    2a) (Niphal) to be hoed\r\n 3) to be lacking, fail\r\n    3a) (Niphal)\r\n        3b1) to be lacking\r\n        3b1) to leave lacking\r\n    3b) (Piel) to leave lacking\r\n'),
(5738, '`Eder', '\\^rde\\^ `Eder', '\\\\eh''-der\\\\', 'from \\\\05737\\\\', '', 'n pr m', 'Ader 1', 1, ' Ader = "flock"\r\n\r\n 1) a Benjamite, son of Beriah, chief of the inhabitants of Aijalon\r\n    1a) also ''Eder''\r\n'),
(5739, '`eder', '\\^rde\\^ `eder', '\\\\ay''-der\\\\', 'from \\\\05737\\\\', 'TWOT - 1572a', 'n m', 'flock 32, drove 4, herds 2', 38, ' 1) flock, herd\r\n    1a) flock\r\n    1b) herds, flocks and herds\r\n'),
(5740, '`Eder', '\\^rde\\^ `Eder', '\\\\ay''-der\\\\', 'the same as \\\\05739\\\\', '', '', 'Eder 3', 3, ' Eder = "a flock"\r\n\r\n n pr m\r\n 1) a Merarite Levite in the time of David\r\n\r\n n pr loc\r\n 2) a town in the extreme south of Judah on the border of Edom; site\r\n    unknown\r\n'),
(5741, '`Adriy''el', '\\^layrde\\^ `Adriy''el', '\\\\ad-ree-ale''\\\\', 'from \\\\05739\\\\ and \\\\0410\\\\', '', 'n pr m', 'Adriel 2', 2, ' Adriel = "flock of God"\r\n\r\n 1) son of Barzillai, and the man to whom Saul gave his daughter Merab\r\n    in marriage although she was already promised to David\r\n'),
(5742, '`adash', '\\^vde\\^ `adash or (plural)  \\^Myvde\\^', '\\\\aw-dawsh''\\\\', 'from an unused root of uncertain meaning', 'TWOT - 1573', 'n f', 'lentiles 4', 4, ' 1) lentil\r\n'),
(5743, '`uwb', '\\^bwe\\^ `uwb', '\\\\oob\\\\', 'a primitive root', 'TWOT - 1574', 'v', 'cover with a cloud 1', 1, ' 1) (Hiphil) to becloud\r\n'),
(5744, '`Owbed', '\\^dbwe\\^ `Owbed', '\\\\o-bade''\\\\', 'act part of \\\\05647\\\\, Greek \\\\5601\\\\ \\~wbhd\\~', '', 'n pr m', 'Obed 10', 10, ' Obed = "serving"\r\n\r\n 1) son of Boaz by Ruth and the grandfather of David\r\n 2) a Judaite, a descendant of Jarha, the Egyptian slave of Sheshan,\r\n    in the line of Jerahmeel\r\n 3) a Judaite, father of Azariah, one of the captains of hundreds who\r\n    joined with Jehoiada in the revolution by which Athaliah fell\r\n 4) one of David''s mighty warriors\r\n 5) son of Shemaiah and a gatekeeper of the temple\r\n'),
(5745, '`Owbal', '\\^lbwe\\^ `Owbal', '\\\\o-bawl''\\\\', 'of foreign derivation', '', 'n pr m', 'Obal 1', 1, ' Obal = "stripped bare"\r\n\r\n 1) a son of Joktan and the founder of an Arab tribe\r\n'),
(5746, '`uwg', '\\^gwe\\^ `uwg', '\\\\oog\\\\', 'a primitive root', 'TWOT - 1575', 'v', 'bake 1', 1, ' 1) (Qal) to bake, bake a cake\r\n'),
(5747, '`Owg', '\\^gwe\\^ `Owg', '\\\\ogue\\\\', 'probably from \\\\05746\\\\', '', 'n pr m', 'Og 22', 22, ' Og = "long-necked"\r\n\r\n 1) the Amorite king of Bashan and one of the last representatives\r\n    of the giants of Rephaim\r\n'),
(5748, '`uwgab', '\\^bgwe\\^ `uwgab or  \\^bge\\^ `uggab', '\\\\oo-gawb''\\\\ \\\\oog-gawb''\\\\', 'from \\\\05689\\\\ in the original sense of breathing', 'TWOT - 1559c', 'n m', 'organ 2, flute 1, pipe 1', 4, ' 1) a musical instrument\r\n    1a) perhaps a flute, reed-pipe, or panpipes\r\n'),
(5749, '`uwd', '\\^dwe\\^ `uwd', '\\\\ood\\\\', 'a primitive root', 'TWOT - 1576,1576d', 'v', 'testify 15, protest 6, witness 6, record 3, charge 2, solemnly 2, take 3, admonished 1, misc 7', 45, ' 1) to return, repeat, go about, do again\r\n    1a) (Piel) to surround, go round and round\r\n    1b) (Pilel) to restore, relieve\r\n    1c) (Hithpalel) to be restored\r\n 2) to bear witness\r\n    2a) (Qal) to bear witness, say again and again\r\n    2b) (Hiphil)\r\n        2b1) to testify, bear witness\r\n        2b2) to cause to testify, take or call as witness, invoke\r\n        2b3) to protest, affirm solemnly, warn, exhort or enjoin solemnly,\r\n             admonish, charge\r\n    2c) (Hophal) to protest, give warning\r\n'),
(5750, '`owd', '\\^dwe\\^ `owd or  \\^de\\^ `od', '\\\\ode\\\\ \\\\ode\\\\', 'from \\\\05749\\\\', 'TWOT - 1576a', 'subst, adv', 'again, more, good while, longer, else, since, yet, still', 30, ' subst\r\n 1) a going round, continuance\r\n\r\n adv\r\n 2) still, yet, again, besides\r\n    2a) still, yet (of continuance or persistence)\r\n    2b) still, yet, more (of addition or repetition)\r\n    2c) again\r\n    2d) still, moreover, besides\r\n'),
(5751, '`owd', '\\^dwe\\^ `owd (Aramaic)', '\\\\ode\\\\', 'corresponding to \\\\05750\\\\', 'TWOT - 2901', 'adv', 'while 1', 1, ' 1) still, while, yet\r\n'),
(5752, '`Owded', '\\^ddwe\\^ `Owded or  \\^dde\\^ `Oded', '\\\\o-dade''\\\\ \\\\o-dade''\\\\', 'from \\\\05749\\\\', '', 'n pr m', 'Oded 3', 3, ' Oded = "restorer"\r\n\r\n 1) father of Azariah the prophet in the reign of king Asa of Judah\r\n 2) a prophet of Jehovah in Samaria at the time of Pekah''s invasion\r\n    of Judah\r\n'),
(5753, '`avah', '\\^hwe\\^ `avah', '\\\\aw-vaw''\\\\', 'a primitive root', 'TWOT - 1577', 'v', 'iniquity 4, perverse 2, perversely 2, perverted 2, amiss 1, turn 1, crooked 1, bowed down 1, troubled 1, wickedly 1, wrong 1', 17, ' 1) to bend, twist, distort\r\n    1a) (Niphal) to be bent, be bowed down, be twisted, be perverted\r\n    1b) (Piel) to twist, distort\r\n    1c) (Hiphil) to do perversely\r\n 2) to commit iniquity, do wrong, pervert\r\n    2a) (Qal) to do wrong, commit iniquity\r\n    2b) (Hiphil) to commit iniquity\r\n'),
(5754, '`avvah', '\\^hwe\\^ `avvah', '\\\\av-vaw''\\\\', 'intensive from \\\\05753\\\\ abbreviated', 'TWOT - 1577b', 'n f', 'overturn 3', 3, ' 1) distortion, ruin\r\n'),
(5755, '`Ivvah', '\\^hwe\\^ `Ivvah or  \\^awe\\^ `Avva'' (\\\\#2Ki 17:24\\\\)', '\\\\iv-vaw''\\\\ \\\\av-vaw''\\\\', 'for \\\\05754\\\\', '', 'n pr loc', 'Ivah 3, Ava 1', 4, ' Ava or Ivah = "ruin"\r\n\r\n 1) a city conquered by the Assyrians\r\n'),
