(6042, '`Unniy', '\\^yne\\^ `Unniy', '\\\\oon-nee''\\\\', 'from \\\\06031\\\\', '', 'n pr m', 'Unni 3', 3, ' Unni = "afflicted"\r\n\r\n 1) a Levite doorkeeper in the time of David\r\n 2) a Levite concerned in the sacred office after the return from\r\n    exile in Babylon\r\n'),
(6043, '`Anayah', '\\^hyne\\^ `Anayah', '\\\\an-aw-yaw''\\\\', 'from \\\\06030\\\\', '', 'n pr m', 'Anaiah 2', 2, ' Anaiah = "Jehovah has answered"\r\n\r\n 1) a priest who helped Ezra and signed the covenant with Nehemiah\r\n'),
