(6067, '`Anath', '\\^tne\\^ `Anath', '\\\\an-awth''\\\\', 'from \\\\06030\\\\', '', 'n pr m', 'Anath 2', 2, ' Anath = "answer"\r\n\r\n 1) father of Shamgar\r\n'),
(6068, '`Anathowth', '\\^twtne\\^ `Anathowth', '\\\\an-aw-thoth''\\\\', 'pl. of \\\\06067\\\\', '', '', 'Anathoth 15', 15, ' Anathoth = "answers to prayer"\r\n\r\n n pr m\r\n 1) son of Becher and grandson of Benjamin\r\n 2) one of the heads of the people who signed the covenant with Nehemiah\r\n\r\n n pr loc\r\n 3) a city of Benjamin allotted to the priest; located approximately 3\r\n    miles (5 km) from Jerusalem; birthplace of the prophet Jeremiah\r\n'),
(6069, '`Anthothiy', '\\^yttne\\^ `Anthothiy or  \\^ytwtne\\^ `Ann@thowthiy', '\\\\an-tho-thee''\\\\ \\\\an-ne-tho-thee''\\\\', 'from \\\\06068\\\\', '', 'adj patr', 'Anethothite 2, Antothite 2, Anathoth 1', 5, ' Anethothite = see Anathoth "affliction"\r\n\r\n 1) a descendant or inhabitant of Anathoth\r\n'),
