(6074, '`ophiy', '\\^ype\\^ `ophiy (Aramaic)', '\\\\of-ee''\\\\', 'corresponding to \\\\06073\\\\', 'TWOT - 2924', 'n m', 'leaves 3', 3, ' 1) leafage, foliage\r\n'),
(6075, '`aphal', '\\^lpe\\^ `aphal', '\\\\aw-fal''\\\\', 'a primitive root', 'TWOT - 1662,1663', 'v', 'lifted up 1, presumed 1', 2, ' 1) to lift up, swell, be lifted up\r\n    1a) (Pual) to swell\r\n    1b) (Hiphil) to be bold, be swelled\r\n 2) to presume, be heedless\r\n    2a) (Hiphil) to be heedless, show heedlessness\r\n'),
(6076, '`ophel', '\\^lpe\\^ `ophel', '\\\\o''-fel\\\\', 'from \\\\06075\\\\', 'TWOT - 1662a,1662b', 'n m', 'forts 1, strong hold 1, tower 1, variant for emerods 6', 9, ' 1) hill, mound, fort, stronghold, Ophel\r\n 2) tumour, hemorrhoid\r\n'),
(6077, '`Ophel', '\\^lpe\\^ `Ophel', '\\\\o''-fel\\\\', 'the same as \\\\06076\\\\', 'TWOT - 1662a', 'n pr loc', 'Ophel 5', 5, ' Ophel = "hill"\r\n\r\n 1) a ridge of hills in Jerusalem, fortified for defense of the city\r\n'),
