(6279, '`athar', '\\^rte\\^ `athar', '\\\\aw-thar''\\\\', 'a primitive root [rather denominative from \\\\06281\\\\]', 'TWOT - 1722', 'v', 'intreat 18, pray 1, prayer 1', 20, ' 1) to pray, entreat, supplicate\r\n    1a) (Qal) to pray, entreat\r\n    1b) (Niphal) to be supplicated, be entreated\r\n    1c) (Hiphil) to make supplication, plead\r\n'),
(6280, '`athar', '\\^rte\\^ `athar', '\\\\aw-thar''\\\\', 'a primitive root', 'TWOT - 1723', 'v', 'deceitful 1, multiplied 1', 2, ' 1) to be abundant\r\n    1a) (Niphal) to be plentiful\r\n    1b) (Hiphil) to multiply, become abundant\r\n'),
