(8173, 'sha`a`', '\\^eev\\^ sha`a`', '\\\\shaw-ah''\\\\', 'a primitive root', 'TWOT - 2435,2436', 'v', 'delight 4, cry 1, cry out 1, play 1, dandle 1, shut 1', 9, ' 1) to stroke, be smeared over, be blinded\r\n    1a) (Qal)\r\n        1a1) to be smeared over, be blinded\r\n        1a2) to smear eyes shut\r\n    1b) (Hiphil) to besmear (of eyes), smear over eyes\r\n    1c) (Hithpalpel) to blind oneself, be blinded\r\n 2) to sport, take delight in\r\n    2a) (Pilpel) to sport, delight in, take delight in, delight oneself\r\n    2b) (Palpal) to be fondled\r\n    2c) (Hithpalpel) to delight oneself\r\n'),
(8174, 'Sha`aph', '\\^Pev\\^ Sha`aph', '\\\\shah''-af\\\\', 'from \\\\05586\\\\', '', 'n pr m', 'Shaaph 2', 2, ' Shaaph = "division"\r\n\r\n 1) son of Jahdai of the family of Caleb, the brother of Jerahmeel\r\n 2) son of Caleb, the brother of Jerahmeel, by his concubine Maachah\r\n    and father of Madmannah\r\n'),
(8175, 'sa`ar', '\\^res\\^ sa`ar', '\\\\saw-ar''\\\\', 'a primitive root', 'TWOT - 2274d,2275,2276', 'verb', 'be (horrible) afraid 4, come like (to take away as with) a whirlwind 2, be tempestuous 1, hurl as a storm 1', 8, ' 1) to storm, shiver, dread, bristle (with horror), be very afraid\r\n    1a) (Qal)\r\n        1a1) to bristle (with horror)\r\n        1a2) to dread\r\n 2) to storm away, sweep away, whirl away\r\n    2a) (Qal)\r\n        2a1) to sweep away\r\n        2a2) to storm away (of God''s action against the wicked) (fig)\r\n    2b) (Niphal) to be stormy, be tempestuous (exceedingly)\r\n    2c) (Piel) to whirl away, be stormed away\r\n    2d) (Hithpael) to storm against, come as a storm\r\n'),
(8176, 'sha`ar', '\\^rev\\^ sha`ar', '\\\\shaw-ar''\\\\', 'primitive root', 'TWOT - 2438', 'v', 'think 1', 1, ' 1) to split open, reason out, calculate, reckon, estimate\r\n    1a) (Qal) to calculate \\\\#Pr 23:7\\\\\r\n'),
