(8198, 'shiphchah', '\\^hxpv\\^ shiphchah', '\\\\shif-khaw''\\\\', 'from an unused root meaning to spread out (as a family; see \\\\04940\\\\)', '', 'n f', 'handmaid 29, maid 12, maidservant 8, bondwomen 3, maiden 3, womenservants 3, handmaidens 2, bondmaid 1, servant 1, wench 1', 63, ' 1) maid, maid-servant, slavegirl\r\n    1a) maid, maid-servant (as belonging to a mistress)\r\n    1b) of address, speaker, humility (fig)\r\n'),
(8199, 'shaphat', '\\^jpv\\^ shaphat', '\\\\shaw-fat''\\\\', 'a primitive root', 'TWOT - 2443', 'v', 'judge (v) 119, judge (n) 60, plead 11, avenged 2, condemn 2, execute 2, judgment 2, defend 1, deliver 1, misc 3', 203, ' 1) to judge, govern, vindicate, punish\r\n    1a) (Qal)\r\n        1a1) to act as law-giver or judge or governor (of God, man)\r\n             1a1a) to rule, govern, judge\r\n        1a2) to decide controversy (of God, man)\r\n        1a3) to execute judgment\r\n             1a3a) discriminating (of man)\r\n             1a3b) vindicating\r\n             1a3c) condemning and punishing\r\n             1a3d) at theophanic advent for final judgment\r\n    1b) (Niphal)\r\n       1b1) to enter into controversy, plead, have controversy together\r\n        1b2) to be judged\r\n    1c) (Poel) judge, opponent-at-law (participle)\r\n'),
(8200, 'sh@phat', '\\^jpv\\^ sh@phat (Aramaic)', '\\\\shef-at''\\\\', 'corresponding to \\\\08199\\\\', 'TWOT - 2045', 'v', 'magistrates 1', 1, ' 1) to judge\r\n    1a) (P''al) judge (participle)\r\n'),
