(8210, 'shaphak', '\\^Kpv\\^ shaphak', '\\\\shaw-fak''\\\\', 'a primitive root', 'TWOT - 2444', 'v', 'pour out 46, shed 36, pour 20, cast 6, gush out 1, misc 6', 115, ' 1) to pour, pour out, spill\r\n    1a) (Qal)\r\n        1a1) to pour, pour out\r\n        1a2) to shed (blood)\r\n        1a3) to pour out (anger or heart) (fig)\r\n    1b) (Niphal) to be poured out, be shed\r\n    1c) (Pual) to be poured out, be shed\r\n    1d) (Hithpael)\r\n        1d1) to be poured out\r\n        1d2) to pour out oneself\r\n'),
(8211, 'shephek', '\\^Kpv\\^ shephek', '\\\\sheh''-fek\\\\', 'from \\\\08210\\\\', 'TWOT - 2444a', 'n m', 'poured out 2', 2, ' 1) place of pouring\r\n'),
(8212, 'shophkah', '\\^hkpv\\^ shophkah', '\\\\shof-kaw''\\\\', 'a derivative from \\\\08210\\\\', 'TWOT - 2444b', 'n f', 'privy member 1', 1, ' 1) penis, urethra, male organ\r\n    1a) as fluid duct\r\n'),
(8213, 'shaphel', '\\^lpv\\^ shaphel', '\\\\shaw-fale''\\\\', 'a primitive root', 'TWOT - 2445', 'v', '...low 10, ...down 8, humble 7, abase 2, debase 1, put lower 1', 29, ' 1) to be or become low, sink, be humbled, be abased\r\n    1a) (Qal) to be or become low\r\n    1b) (Hiphil)\r\n        1b1) to lay or bring low, humiliate\r\n        1b2) to set in a lower place, show abasement\r\n        1b3) to make low, sit down\r\n'),
(8214, 'sh@phal', '\\^lpv\\^ sh@phal (Aramaic)', '\\\\shef-al''\\\\', 'corresponding to \\\\08213\\\\', '', 'v', 'humble 1, abase 1, subdue 1, put down 1', 4, ' 1) to be or bring low, humble\r\n    1a) (Aphel) to bring low, humble\r\n'),
(8215, 'sh@phal', '\\^lpv\\^ sh@phal (Aramaic)', '\\\\shef-al''\\\\', 'from \\\\08214\\\\', '', 'adj', 'basest 1', 1, ' 1) low, lowliest (of station)\r\n'),
